import xml.etree.ElementTree as ET
from collections import deque
import json

root = ET.fromstring(map_xml_string)
width = int(root.get('width'))
height = int(root.get('height'))
grid = [['' for _ in range(width)] for _ in range(height)]
impassable_tiles = set()

for r_idx, row_elem in enumerate(root.findall('Row')):
    y = int(row_elem.get('id')) - 1
    for c_idx, tile_elem in enumerate(row_elem.findall('Tile')):
        x = int(tile_elem.get('id')) - 1
        tile_type = tile_elem.get('type')
        grid[y][x] = tile_type
        if tile_type in ['impassable', 'boulder_barrier', 'closed_gate'] or tile_elem.find('Object') is not None:
            impassable_tiles.add((x, y))

boulder_start_x, boulder_start_y = int(input_data['boulder_start_x']) - 1, int(input_data['boulder_start_y']) - 1
hole_x, hole_y = int(input_data['hole_x']) - 1, int(input_data['hole_y']) - 1
player_x, player_y = int(input_data['player_x']) - 1, int(input_data['player_y']) - 1

def bfs_path(start, end, obstacles):
    queue = deque([(start, [])])
    visited = {start}
    while queue:
        (cx, cy), path = queue.popleft()
        if (cx, cy) == end:
            return path
        for dx, dy in [(0, 1), (0, -1), (1, 0), (-1, 0)]:
            nx, ny = cx + dx, cy + dy
            if (0 <= nx < width and 0 <= ny < height and (nx, ny) not in visited and (nx, ny) not in obstacles):
                visited.add((nx, ny))
                queue.append(((nx, ny), path + [(nx, ny)]))
    return None

def find_boulder_path():
    initial_state = (player_x, player_y, boulder_start_x, boulder_start_y)
    queue = deque([(initial_state, [])])
    visited_states = {initial_state}

    while queue:
        (px, py, bx, by), plan = queue.popleft()

        if (bx, by) == (hole_x, hole_y):
            return plan

        for move_dir in [('Up', 0, -1), ('Down', 0, 1), ('Left', -1, 0), ('Right', 1, 0)]:
            move_name, dx, dy = move_dir
            player_push_pos = (bx - dx, by - dy)

            if not (0 <= player_push_pos[0] < width and 0 <= player_push_pos[1] < height):
                continue

            path_to_push_pos = bfs_path((px, py), player_push_pos, impassable_tiles | {(bx, by)})

            if path_to_push_pos is not None:
                new_bx, new_by = bx + dx, by + dy

                if not (0 <= new_bx < width and 0 <= new_by < height) or (new_bx, new_by) in impassable_tiles:
                    continue

                new_state = (bx, by, new_bx, new_by)
                if new_state not in visited_states:
                    visited_states.add(new_state)
                    new_plan = plan + [{'path_to_push': [{'x': x+1, 'y': y+1} for x, y in path_to_push_pos], 'push_direction': move_name}]
                    queue.append((new_state, new_plan))

    return None

path_result = find_boulder_path()
if path_result:
    print(json.dumps({'plan': path_result, 'reasoning': 'Found a valid plan to push the boulder.'}))
else:
    print(json.dumps({'plan': [], 'reasoning': 'Could not find a valid plan to push the boulder.'}))

