import xml.etree.ElementTree as ET
from collections import deque
import json

root = ET.fromstring(map_xml_string)
width = int(root.get('width'))
height = int(root.get('height'))
grid = [['' for _ in range(width)] for _ in range(height)]
impassable_coords = set()
unseen_tiles = []

movement_mode = input_data['movement_mode']

if movement_mode == 'walking':
    traversable_types = {'ground', 'grass', 'open_gate', 'cleared_boulder_barrier', 'boulder_switch', 'teleport', 'hole', 'spinner_stop', 'cuttable', 'elevated_ground', 'ladder_up', 'ladder_down', 'steps'}
elif movement_mode == 'surfing':
    traversable_types = {'water'}
else:
    traversable_types = set()

for r_idx, row_elem in enumerate(root.findall('Row')):
    y = int(row_elem.get('id')) - 1
    for c_idx, tile_elem in enumerate(row_elem.findall('Tile')):
        x = int(tile_elem.get('id')) - 1
        tile_type = tile_elem.get('type')
        grid[y][x] = tile_type
        if tile_elem.get('seen') == 'false':
            unseen_tiles.append((x, y))
        if tile_elem.find('Object') is not None and (tile_elem.find('Object').get('id-name') != 'Pikachu'):
            impassable_coords.add((x, y))

start_x, start_y = int(input_data['start_x']) - 1, int(input_data['start_y']) - 1
end_x = int(input_data.get('end_x', None)) - 1 if input_data.get('end_x') else None
end_y = int(input_data.get('end_y', None)) - 1 if input_data.get('end_y') else None

def is_target_adjacent(current_x, current_y, target_x, target_y):
    for dx, dy in [(0, 1), (0, -1), (1, 0), (-1, 0)]:
        if current_x == target_x + dx and current_y == target_y + dy:
            return True
    return False

def find_path_bfs():
    if (start_x, start_y) in impassable_coords or grid[start_y][start_x] not in traversable_types:
        return []

    queue = deque([((start_x, start_y), [(start_x, start_y)])])
    visited = {(start_x, start_y)}

    target_is_impassable = False
    if end_x is not None and end_y is not None:
        if not (0 <= end_x < width and 0 <= end_y < height) or (end_x, end_y) in impassable_coords or grid[end_y][end_x] not in traversable_types:
            target_is_impassable = True

    while queue:
        (current_x, current_y), path = queue.popleft()
        current_tile_type = grid[current_y][current_x]

        if end_x is not None and end_y is not None:
            if not target_is_impassable:
                if current_x == end_x and current_y == end_y:
                    return path
            else:
                if is_target_adjacent(current_x, current_y, end_x, end_y):
                    return path
        elif unseen_tiles:
            for ux, uy in unseen_tiles:
                if is_target_adjacent(current_x, current_y, ux, uy) and grid[uy][ux] in traversable_types:
                    return path

        for move in [(0, 1), (0, -1), (1, 0), (-1, 0)]:
            next_x, next_y = current_x + move[0], current_y + move[1]

            if not (0 <= next_x < width and 0 <= next_y < height) or (next_x, next_y) in visited:
                continue

            next_tile_type = grid[next_y][next_x]

            if current_tile_type == 'steps' and next_tile_type in ['ground', 'elevated_ground']:
                pass 
            elif next_tile_type == 'steps' and current_tile_type in ['ground', 'elevated_ground']:
                pass
            elif current_tile_type in ['ground', 'elevated_ground'] and next_tile_type in ['ground', 'elevated_ground'] and current_tile_type != next_tile_type:
                continue

            if next_tile_type == 'ledge':
                if next_y > current_y:
                    ledge_jump_y = next_y + 1
                    if 0 <= ledge_jump_y < height and (next_x, ledge_jump_y) not in visited and (next_x, ledge_jump_y) not in impassable_coords:
                        new_path = path + [(next_x, next_y), (next_x, ledge_jump_y)]
                        visited.add((next_x, next_y))
                        visited.add((next_x, ledge_jump_y))
                        queue.append(((next_x, ledge_jump_y), new_path))
                continue

            if (next_x, next_y) in impassable_coords or next_tile_type not in traversable_types:
                continue

            visited.add((next_x, next_y))
            new_path = path + [(next_x, next_y)]
            queue.append(((next_x, next_y), new_path))

    return []

found_path = find_path_bfs()

if found_path:
    path_plan = [{'x': x + 1, 'y': y + 1} for x, y in found_path]
    target_x, target_y = path_plan[-1]['x'], path_plan[-1]['y']
    reasoning = f'Path found to target at ({target_x}, {target_y}).'
    print(json.dumps({'path': path_plan, 'reasoning': reasoning}))
else:
    print(json.dumps({'path': [], 'reasoning': 'Could not find a path to any reachable targets.'}))
