import json
import heapq
import xml.etree.ElementTree as ET

def find_path_astar(map_xml_string, start_x, start_y, end_x, end_y, temp_impassable_coords_str=None):
    root = ET.fromstring(map_xml_string)
    map_width = int(root.get('width'))
    map_height = int(root.get('height'))

    tiles = {}
    for row in root.findall('Row'):
        row_id = int(row.get('id'))
        for tile_elem in row.findall('Tile'):
            tile_id = int(tile_elem.get('id'))
            x, y = tile_id, row_id
            tile_type = tile_elem.get('type')
            has_object = tile_elem.get('has-object') == 'true'
            object_name = None
            object_id = None
            for obj_elem in tile_elem.findall('Object'):
                object_name = obj_elem.get('name') or obj_elem.get('id-name')
                object_id = obj_elem.get('id')
                break
            tiles[(x, y)] = {'type': tile_type, 'has_object': has_object, 'object_name': object_name, 'object_id': object_id}

    temp_impassable_coords = set()
    if temp_impassable_coords_str:
        try:
            temp_list = json.loads(temp_impassable_coords_str)
            for coord_pair in temp_list:
                if isinstance(coord_pair, list) and len(coord_pair) == 2:
                    temp_impassable_coords.add(tuple(coord_pair))
        except json.JSONDecodeError:
            pass

    start = (int(start_x), int(start_y))
    end = (int(end_x), int(end_y))

    traversable_tiles = {'ground', 'grass', 'steps', 'elevated_ground', 'cleared_boulder_barrier', 'water', 'ladder_up', 'ladder_down'}

    def is_traversable(x, y):
        if not (1 <= x <= map_width and 1 <= y <= map_height):
            return False, 'Map boundary'

        tile_info = tiles.get((x, y))
        if not tile_info:
            return False, 'Unknown tile'

        tile_type = tile_info['type']
        has_object = tile_info['has_object']
        object_name = tile_info['object_name']
        object_id = tile_info['object_id']

        if (x, y) in temp_impassable_coords:
            return False, f'Temporary impassable tile at ({x}, {y})'

        # Refactored logic with explicit if/elif
        if tile_type in {'impassable', 'boulder_barrier', 'closed_gate'}:
            return False, f'Impassable terrain type \"{tile_type}\" at ({x}, {y})'
        elif has_object and object_id != '15':
            return False, f'Object \"{object_name}\" at ({x}, {y})'
        elif tile_type in traversable_tiles or tile_type == 'ledge':
            return True, None
        else:
            return False, f'Unhandled or impassable terrain type \"{tile_type}\" at ({x}, {y})'

    def get_cost(current, next_tile):
        return 1

    def heuristic(a, b):
        return abs(a[0] - b[0]) + abs(a[1] - b[1])

    open_set = []
    heapq.heappush(open_set, (0, start))

    came_from = {}
    g_score = {tile: float('inf') for tile in tiles}
    g_score[start] = 0
    f_score = {tile: float('inf') for tile in tiles}
    f_score[start] = heuristic(start, end)

    closed_set = set()
    blocking_obstacles_on_frontier = set()

    while open_set:
        current_f_score, current = heapq.heappop(open_set)

        if current in closed_set:
            continue
        closed_set.add(current)

        if current == end:
            path = []
            while current in came_from:
                path.append(current)
                current = came_from[current]
            path.append(start)
            print(json.dumps({'path_found': True, 'path': path[::-1]}))
            return

        for dx, dy in [(0, 1), (0, -1), (1, 0), (-1, 0)]:
            neighbor = (current[0] + dx, current[1] + dy)

            is_neighbor_traversable, blocking_reason = is_traversable(neighbor[0], neighbor[1])

            current_tile_type = tiles.get(current, {}).get('type')
            neighbor_tile_type = tiles.get(neighbor, {}).get('type')

            if current_tile_type == 'ledge' and dy == -1:
                continue

            if neighbor_tile_type == 'ledge' and dy == 1:
                pass
            elif neighbor_tile_type == 'ledge' and dy != 1:
                continue

            if not is_neighbor_traversable:
                if blocking_reason:
                    blocking_obstacles_on_frontier.add(blocking_reason)
                continue

            tentative_g_score = g_score[current] + get_cost(current, neighbor)

            if tentative_g_score < g_score.get(neighbor, float('inf')):
                came_from[neighbor] = current
                g_score[neighbor] = tentative_g_score
                f_score[neighbor] = tentative_g_score + heuristic(neighbor, end)
                if neighbor not in [i[1] for i in open_set]:
                    heapq.heappush(open_set, (f_score[neighbor], neighbor))

    error_msg = 'No path found. The destination may be in a disconnected area of the map.'
    if blocking_obstacles_on_frontier:
        error_msg = f"Path blocked. Obstacles on search frontier: {', '.join(sorted(list(blocking_obstacles_on_frontier)))}"

    print(json.dumps({'path_found': False, 'error': error_msg}))

