import json
import xml.etree.ElementTree as ET
import heapq
import traceback

def main():
    try:
        map_xml_string = globals().get('map_xml_string')
        input_data = globals().get('input_data')
        num_turns_str = input_data['num_turns']
        badges_obtained_json = input_data['badges_obtained_json']

        root = ET.fromstring(map_xml_string)
        map_id = int(root.get('id'))
        map_width = int(root.get('width'))
        map_height = int(root.get('height'))
        num_turns = int(num_turns_str)
        badges_obtained = json.loads(badges_obtained_json)

        player_x, player_y = -1, -1
        unseen_tiles = []
        all_tiles = {}
        traversable_types = {'ground', 'grass', 'steps', 'elevated_ground', 'cleared_boulder_barrier', 'water', 'ladder_up', 'ladder_down'}

        for row_elem in root.findall('Row'):
            y = int(row_elem.get('id'))
            for tile_elem in row_elem.findall('Tile'):
                x = int(tile_elem.get('id'))
                tile_type = tile_elem.get('type')
                has_object = tile_elem.get('has-object') == 'true'
                object_name, object_id = None, None
                obj_elem = tile_elem.find('Object')
                if obj_elem is not None:
                    object_name = obj_elem.get('name') or obj_elem.get('id-name')
                    object_id = obj_elem.get('id')
                all_tiles[(x, y)] = {'type': tile_type, 'has_object': has_object, 'object_name': object_name, 'object_id': object_id}
                if tile_elem.get('has-player') == 'true':
                    player_x, player_y = x, y
                if tile_elem.get('seen') == 'false':
                    unseen_tiles.append((x, y))

        def a_star_search(start, goal_condition, ignore_objects=False):
            open_set = [(0, start)]
            came_from = {}
            g_score = {start: 0}
            closed_set = set()

            while open_set:
                _, current = heapq.heappop(open_set)

                if goal_condition(current):
                    return True

                if current in closed_set:
                    continue
                closed_set.add(current)

                for dx, dy in [(0, 1), (0, -1), (1, 0), (-1, 0)]:
                    neighbor = (current[0] + dx, current[1] + dy)
                    if not (1 <= neighbor[0] <= map_width and 1 <= neighbor[1] <= map_height):
                        continue

                    tile_info = all_tiles.get(neighbor)
                    if not tile_info:
                        continue

                    tile_type = tile_info['type']
                    current_tile_type = all_tiles.get(current, {}).get('type')

                    if tile_type == 'ledge' and current_tile_type != 'ledge' and dy != 1:
                        continue
                    if current_tile_type == 'ledge' and tile_type == 'ledge' and dx != 0:
                        continue

                    is_traversable_type = tile_type in traversable_types or tile_type == 'ledge'
                    has_blocking_object = tile_info['has_object'] and tile_info['object_id'] != '15'

                    if not is_traversable_type or tile_type in {'impassable', 'boulder_barrier', 'closed_gate'} or (has_blocking_object and not ignore_objects):
                        continue

                    tentative_g_score = g_score[current] + 1
                    if tentative_g_score < g_score.get(neighbor, float('inf')):
                        came_from[neighbor] = current
                        g_score[neighbor] = tentative_g_score
                        f_score = tentative_g_score + abs(neighbor[0] - start[0]) + abs(neighbor[1] - start[1])
                        heapq.heappush(open_set, (f_score, neighbor))
            return False

        navigable_warps = []
        for tile_elem in root.findall('.//Tile[@is-warp="true"]'):
            if tile_elem.find('Warp') is not None:
                coord_str = tile_elem.get('coordinate')
                try:
                    x_str = coord_str.split('(')[1].split(',')[0]
                    y_str = coord_str.split(',')[1].split(')')[0].strip()
                    warp_x, warp_y = int(x_str), int(y_str)
                    if a_star_search((player_x, player_y), lambda pos: pos == (warp_x, warp_y)):
                        navigable_warps.append({'x': warp_x, 'y': warp_y})
                except (IndexError, ValueError):
                    continue

        reachable_unseen_count = 0
        for x, y in unseen_tiles:
            for dx, dy in [(0, 1), (0, -1), (1, 0), (-1, 0)]:
                adj_x, adj_y = x + dx, y + dy
                adj_tile = all_tiles.get((adj_x, adj_y))
                if adj_tile and (adj_tile['type'] in traversable_types or adj_tile['type'] == 'ledge'):
                    if a_star_search((player_x, player_y), lambda pos: pos == (adj_x, adj_y), ignore_objects=True):
                        reachable_unseen_count += 1
                        break

        warp_groups = []
        sorted_warps = sorted(navigable_warps, key=lambda w: (w['y'], w['x']))
        for warp in sorted_warps:
            is_grouped = False
            for group in warp_groups:
                if any(abs(warp['x'] - gw['x']) <= 1 and abs(warp['y'] - gw['y']) <= 1 for gw in group):
                    group.append(warp); is_grouped = True; break
            if not is_grouped: warp_groups.append([warp])

        is_in_dead_end_area = reachable_unseen_count == 0 and (len(warp_groups) + 0) < 2

        result = {
            'current_map_id': map_id,
            'current_position': {'x': player_x, 'y': player_y},
            'num_turns': num_turns,
            'badges_obtained': sorted(badges_obtained),
            'is_in_dead_end_area': is_in_dead_end_area,
            'navigable_warps': sorted(navigable_warps, key=lambda w: (w['y'], w['x'])),
            'reachable_unseen_tiles_count': reachable_unseen_count
        }
        print(json.dumps(result))
    except Exception as e:
        error_report = {
            'tool_error': True,
            'error_type': type(e).__name__,
            'error_message': str(e),
            'traceback': traceback.format_exc()
        }
        print(json.dumps(error_report))

main()

