import json
import re
import traceback

def auto_battle(player_party_json, enemy_pokemon_json, active_pokemon_name, screen_text):
    try:
        POKEMON_TYPES = {
            'MAGNEMITE': ['Electric', None], 'KANGASKHAN': ['Normal', None], 'LAPRAS': ['Water', 'Ice'], 'GOLEM': ['Rock', 'Ground'],
            'PIKACHU': ['Electric', None], 'GOLBAT': ['Poison', 'Flying'], 'RHYHORN': ['Rock', 'Ground']
        }
        MOVE_TYPES = {
            'DOUBLE-EDGE': 'Normal', 'SURF': 'Water', 'STRENGTH': 'Normal', 'BODY SLAM': 'Normal', 'ICE BEAM': 'Ice',
            'CONFUSE RAY': 'Ghost', 'SLUDGE': 'Poison', 'FLY': 'Flying', 'HAZE': 'Ice', 'THUNDERBOLT': 'Electric',
            'FLASH': 'Normal', 'LIGHT SCREEN': 'Psychic', 'THUNDER WAVE': 'Electric'
        }
        TYPE_EFFECTIVENESS = {
            'Water': {'super_effective': ['Rock/Ground', 'Ground']},
            'Ground': {'super_effective': ['Electric']},
            'Normal': {'immune': ['Ghost']}
        }

        player_party = json.loads(player_party_json)
        enemy_pokemon = json.loads(enemy_pokemon_json)[0]

        active_pokemon_data = next((p for p in player_party if p['name'] == active_pokemon_name), None)
        if not active_pokemon_data:
            print(json.dumps(['A']))
            return

        enemy_types = POKEMON_TYPES.get(enemy_pokemon['species'], [None, None])

        best_move_name = None
        max_effectiveness = -1

        for move_dict in active_pokemon_data['moves']:
            if move_dict['pp'] > 0:
                move_name = move_dict['name']
                move_type = MOVE_TYPES.get(move_name)
                if not move_type: continue

                effectiveness = 1.0
                rules = TYPE_EFFECTIVENESS.get(move_type, {})
                if enemy_types[0] in rules.get('super_effective', []):
                    effectiveness = 2.0

                if effectiveness > max_effectiveness:
                    max_effectiveness = effectiveness
                    best_move_name = move_name

        if not best_move_name:
            best_move_name = next((m['name'] for m in active_pokemon_data['moves'] if m['pp'] > 0), None)

        if not best_move_name:
            print(json.dumps(['A']))
            return

        lines = screen_text.strip().split('\n')
        if any("FIGHT" in line and "PKMN" in line for line in lines):
            print(json.dumps(['A']))
            return

        cursor_pos = -1
        moves_on_screen = []
        raw_moves = []
        for i, line in enumerate(lines):
            clean_line = line.strip()
            if 'TYPE/' in clean_line or 'PP ' in clean_line:
                continue
            if clean_line.startswith('â'):
                cursor_pos = len(raw_moves) 
            raw_moves.extend(re.split(r'\s{2,}', clean_line.replace('â', '').strip()))

        moves_on_screen = [move for move in raw_moves if move]

        if cursor_pos == -1 or not moves_on_screen:
            print(json.dumps(['A']))
            return

        try:
            # This logic assumes the cursor is always on the first move of a line with two moves.
            # The game state does not provide enough info to distinguish otherwise.
            actual_cursor_index = cursor_pos 
            target_index = moves_on_screen.index(best_move_name)
        except (ValueError, IndexError):
            print(json.dumps(['A'])) # Failsafe
            return

        # Simple vertical navigation
        presses = []
        if target_index == 1: presses.append('Right')
        elif target_index == 2: presses.append('Down')
        elif target_index == 3: presses.extend(['Down', 'Right'])

        # This is a simplification. A real implementation would need to map index to button presses.
        # Let's refine based on a 2x2 grid.
        # 0: (0,0), 1: (1,0), 2: (0,1), 3: (1,1)
        current_x, current_y = (0, 0) # Assume cursor starts at index 0
        target_x, target_y = target_index % 2, target_index // 2

        button_presses = []
        if target_x > current_x: button_presses.append('Right')
        if target_y > current_y: button_presses.append('Down')

        button_presses.append('A')
        print(json.dumps(button_presses))

    except Exception as e:
        error_report = {
            'tool_error': True,
            'error_type': type(e).__name__,
            'error_message': str(e),
            'traceback': traceback.format_exc()
        }
        print(json.dumps(error_report))

auto_battle(
    input_data['player_party_json'],
    input_data['enemy_pokemon_json'],
    input_data['active_pokemon_name'],
    input_data['screen_text']
)
