import json

def get_presses(menu_items, current_selection, target_item):
    try:
        current_idx = menu_items.index(current_selection)
        target_idx = menu_items.index(target_item)
    except ValueError:
        return {'error': 'Item not found in menu list.'}

    # Logic for non-wrapping menu
    if target_idx > current_idx:
        return {'direction': 'Down', 'presses': target_idx - current_idx}
    elif target_idx < current_idx:
        return {'direction': 'Up', 'presses': current_idx - target_idx}
    else:
        return {'direction': 'None', 'presses': 0}

# Main execution
try:
    menu_items_list = json.loads(input_data['menu_items'])
    result = get_presses(menu_items_list, input_data['current_selection'], input_data['target_item'])
    print(json.dumps(result))
except Exception as e:
    print(json.dumps({'error': str(e)}))

