import xml.etree.ElementTree as ET
import heapq

def parse_map_xml(xml_string):
    root = ET.fromstring(xml_string)
    width = int(root.get('width'))
    height = int(root.get('height'))
    grid = [['' for _ in range(width)] for _ in range(height)]
    objects = {}
    for row_elem in root.findall('Row'):
        y = int(row_elem.get('id')) - 1
        for tile_elem in row_elem.findall('Tile'):
            x = int(tile_elem.get('id')) - 1
            grid[y][x] = tile_elem.get('type')
            obj_node = tile_elem.find('Object')
            if obj_node is not None and obj_node.get('id-name') != 'Pikachu':
                objects[(x + 1, y + 1)] = True
    return grid, objects, width, height

def is_valid(x, y, width, height):
    return 1 <= x <= width and 1 <= y <= height

def heuristic(a, b):
    return abs(a[0] - b[0]) + abs(a[1] - b[1])

def get_neighbors(current_pos, grid, objects, width, height, movement_type):
    x, y = current_pos
    current_tile_type = grid[y-1][x-1]
    neighbors = []
    standard_walkable = ['ground', 'grass', 'cleared_boulder_barrier', 'open_gate', 'gate_offscreen', 'boulder_switch', 'spinner_stop', 'ladder_up', 'ladder_down']

    for dx, dy, move in [(0, -1, 'Up'), (0, 1, 'Down'), (-1, 0, 'Left'), (1, 0, 'Right')]:
        nx, ny = x + dx, y + dy
        if not is_valid(nx, ny, width, height):
            continue

        neighbor_pos = (nx, ny)
        if neighbor_pos in objects:
            continue

        neighbor_tile_type = grid[ny-1][nx-1]

        if neighbor_tile_type == 'ledge':
            if dy == 1:
                if is_valid(nx, ny + 1, width, height):
                    landing_pos = (nx, ny + 1)
                    if landing_pos in objects: continue
                    landing_tile_type = grid[ny][nx-1]
                    if movement_type == 'walking' and (landing_tile_type in standard_walkable or landing_tile_type in ['steps', 'elevated_ground']):
                         neighbors.append((landing_pos, move, 1))
                    elif movement_type == 'surfing' and landing_tile_type == 'water':
                         neighbors.append((landing_pos, move, 1))
            continue

        can_traverse = False
        if movement_type == 'walking':
            if current_tile_type in standard_walkable and neighbor_tile_type in standard_walkable:
                can_traverse = True
            elif current_tile_type == 'elevated_ground' and neighbor_tile_type == 'elevated_ground':
                can_traverse = True
            elif (current_tile_type == 'steps' and neighbor_tile_type in ['ground', 'elevated_ground']) or \
                 (neighbor_tile_type == 'steps' and current_tile_type in ['ground', 'elevated_ground']):
                can_traverse = True
            elif neighbor_tile_type.startswith('spinner'):
                can_traverse = True

        elif movement_type == 'surfing':
            if neighbor_tile_type == 'water' or neighbor_tile_type in standard_walkable or neighbor_tile_type in ['steps', 'elevated_ground']:
                can_traverse = True

        if can_traverse:
            neighbors.append((neighbor_pos, move, 1))

    return neighbors

def a_star_search(start, target, grid, objects, width, height, movement_type):
    walkable_land_tiles = ['ground', 'grass', 'steps', 'elevated_ground', 'cleared_boulder_barrier', 'open_gate', 'gate_offscreen', 'boulder_switch', 'spinner_stop', 'ladder_up', 'ladder_down']

    def is_traversable(pos):
        if not is_valid(pos[0], pos[1], width, height): return False
        if pos in objects: return False
        tile_type = grid[pos[1]-1][pos[0]-1]
        if movement_type == 'walking': return tile_type in walkable_land_tiles or tile_type.startswith('spinner')
        elif movement_type == 'surfing': return tile_type == 'water' or tile_type in walkable_land_tiles
        return False

    if is_traversable(target):
        return a_star_search_single(start, target, grid, objects, width, height, movement_type)
    else:
        best_path = None
        min_len = float('inf')
        adjacent_tiles = []
        for dx, dy in [(0, 1), (0, -1), (1, 0), (-1, 0)]:
            adj_target = (target[0] + dx, target[1] + dy)
            if is_traversable(adj_target):
                adjacent_tiles.append(adj_target)

        if not adjacent_tiles:
            return None

        for adj_target in adjacent_tiles:
            path = a_star_search_single(start, adj_target, grid, objects, width, height, movement_type)
            if path and len(path) < min_len:
                min_len = len(path)
                best_path = path
        return best_path

def a_star_search_single(start, target, grid, objects, width, height, movement_type):
    open_set = [(0, start)]
    came_from = {}
    g_score = {start: 0}
    f_score = {start: heuristic(start, target)}
    while open_set:
        _, current = heapq.heappop(open_set)
        if current == target:
            return reconstruct_path(came_from, current)
        for neighbor_pos, move, cost in get_neighbors(current, grid, objects, width, height, movement_type):
            tentative_g_score = g_score[current] + cost
            if tentative_g_score < g_score.get(neighbor_pos, float('inf')):
                came_from[neighbor_pos] = (current, move)
                g_score[neighbor_pos] = tentative_g_score
                f_score[neighbor_pos] = tentative_g_score + heuristic(neighbor_pos, target)
                heapq.heappush(open_set, (f_score[neighbor_pos], neighbor_pos))
    return None

def reconstruct_path(came_from, current):
    path = []
    while current in came_from:
        current, move = came_from[current]
        path.append(move)
    return path[::-1]

