import xml.etree.ElementTree as ET
import heapq

def find_path():
    try:
        start_x = int(input_data['start_x'])
        start_y = int(input_data['start_y'])
        target_x = int(input_data['target_x'])
        target_y = int(input_data['target_y'])
        root = ET.fromstring(map_xml_string)
        width = int(root.get('width'))
        height = int(root.get('height'))
        grid = [[0 for _ in range(width + 1)] for _ in range(height + 1)]
        for row_elem in root.findall('Row'):
            y = int(row_elem.get('id'))
            for tile_elem in row_elem.findall('Tile'):
                x = int(tile_elem.get('id'))
                tile_type = tile_elem.get('type')
                is_impassable = tile_type in ['impassable', 'unknown', 'closed_gate', 'gate_offscreen']
                if tile_elem.find('Object') is not None and tile_elem.find('Object').get('id-name') != 'PIKACHU':
                    is_impassable = True
                if tile_type == 'ledge':
                    if y > 1 and grid[y - 1][x] == 0: # Can only be traversed from above
                        pass
                    else:
                        is_impassable = True 
                if is_impassable:
                    grid[y][x] = 1
        start_node = (start_y, start_x)
        end_node = (target_y, target_x)
        open_list = [(0, start_node)]
        came_from = {}
        g_score = { (r, c): float('inf') for r in range(height + 1) for c in range(width + 1) }
        g_score[start_node] = 0
        f_score = { (r, c): float('inf') for r in range(height + 1) for c in range(width + 1) }
        f_score[start_node] = abs(start_node[0] - end_node[0]) + abs(start_node[1] - end_node[1])
        while open_list:
            _, current_node = heapq.heappop(open_list)
            if current_node == end_node:
                path = []
                while current_node in came_from:
                    path.append(current_node)
                    current_node = came_from[current_node]
                path.append(start_node)
                path_coords = [(c, r) for r, c in path[::-1]]
                print(';'.join([f'{x},{y}' for x, y in path_coords]))
                return
            for dr, dc in [(0, 1), (0, -1), (1, 0), (-1, 0)]:
                neighbor = (current_node[0] + dr, current_node[1] + dc)
                if not (1 <= neighbor[0] <= height and 1 <= neighbor[1] <= width):
                    continue
                if grid[neighbor[0]][neighbor[1]] == 1:
                    continue
                tentative_g_score = g_score[current_node] + 1
                if tentative_g_score < g_score.get(neighbor, float('inf')):
                    came_from[neighbor] = current_node
                    g_score[neighbor] = tentative_g_score
                    f_score[neighbor] = g_score[neighbor] + abs(neighbor[0] - end_node[0]) + abs(neighbor[1] - end_node[1])
                    if neighbor not in [i[1] for i in open_list]:
                        heapq.heappush(open_list, (f_score[neighbor], neighbor))
        print("No path found")
    except Exception as e:
        print(f"Pathfinder Error: {e}")
find_path()
