import xml.etree.ElementTree as ET
import json
import heapq

def solve():
    try:
        root = ET.fromstring(map_xml_string)
        tiles = {}
        objects = set()
        warps = set()

        initial_surfing = bool(input_data.get('is_surfing', False))

        for row in root.findall('Row'):
            y = int(row.attrib['id'])
            for tile in row.findall('Tile'):
                x = int(tile.attrib['id'])
                t_type = tile.attrib['type'].upper()
                tiles[(x, y)] = t_type
                if tile.find('Object') is not None:
                    objects.add((x, y))
                if tile.attrib.get('is-warp') == 'true':
                    warps.add((x, y))

        start = (int(input_data['start_x']), int(input_data['start_y']))
        end = (int(input_data['end_x']), int(input_data['end_y']))

        def get_neighbors(pos, surfing):
            x, y = pos
            res = []
            for dx, dy, move in [(0, 1, 'Down'), (0, -1, 'Up'), (1, 0, 'Right'), (-1, 0, 'Left')]:
                nx, ny = x + dx, y + dy
                if (nx, ny) not in tiles: continue
                ntype = tiles[(nx, ny)]
                if ntype == 'UNSEEN':
                    ntype = 'WATER' if surfing else 'FLOOR'

                ctype = tiles[pos]
                if ctype == 'UNSEEN': ctype = 'WATER' if surfing else 'FLOOR'

                if ntype in ['WALL', 'VOID', 'BUOY']: continue
                if (nx, ny) in objects and (nx, ny) != end: continue
                if (nx, ny) in warps and (nx, ny) != end: continue

                if ntype == 'LEDGE_HOP_DOWN' and move != 'Down': continue
                if ctype == 'LEDGE_HOP_DOWN' and move != 'Down': continue
                if ntype == 'LEDGE_HOP_RIGHT' and move != 'Right': continue
                if ctype == 'LEDGE_HOP_RIGHT' and move != 'Right': continue

                if ntype == 'FLOOR_UP_WALL' and move == 'Down': continue
                if ctype == 'FLOOR_UP_WALL' and move == 'Up': continue

                next_surfing = surfing
                if surfing:
                    if ntype == 'WATER':
                        pass
                    elif ntype in ['FLOOR', 'TALL_GRASS', 'FLOOR_UP_WALL', 'LEDGE_HOP_DOWN', 'LEDGE_HOP_RIGHT']:
                        next_surfing = False
                    else:
                        continue
                else:
                    if ntype == 'WATER':
                        continue

                res.append(((nx, ny), next_surfing, move))
            return res

        queue = [(0, start, initial_surfing, [])]
        visited = {(start, initial_surfing)}
        while queue: 
            dist, curr, surfing, path = heapq.heappop(queue)
            if curr == end:
                print(json.dumps(path[:50]))
                return
            for next_pos, next_surfing, move in get_neighbors(curr, surfing):
                if (next_pos, next_surfing) not in visited:
                    visited.add((next_pos, next_surfing))
                    heapq.heappush(queue, (dist + 1, next_pos, next_surfing, path + [move]))
        print(json.dumps([]))
    except Exception as e:
        print(json.dumps(["Error: " + str(e)]))

solve()

