import xml.etree.ElementTree as ET
import heapq
import json

def find_path(input_data):
    def parse_map(xml_string):
        root = ET.fromstring(xml_string)
        grid = {}
        player_pos = None
        for row_elem in root.findall('Row'):
            y = int(row_elem.get('id'))
            for tile_elem in row_elem.findall('Tile'):
                x = int(tile_elem.get('id'))
                tile_type = tile_elem.get('type')
                has_object = tile_elem.get('has-object') == 'true'
                is_warp = tile_elem.get('is-warp') == 'true'
                object_id_name = None
                obj_elem = tile_elem.find('Object')
                if obj_elem is not None:
                    object_id_name = obj_elem.get('id-name')
                if tile_elem.find('Player') is not None:
                    player_pos = (x, y)
                grid[(x, y)] = {'type': tile_type, 'has_object': has_object, 'is_warp': is_warp, 'object_id_name': object_id_name}
        return grid, player_pos, int(root.get('width')), int(root.get('height'))

    def has_hm(party, hm_name):
        return any(hm_name == move['name'] for pokemon in party if not pokemon.get('is_fainted', False) for move in pokemon.get('moves', []))

    def is_traversable(from_x, from_y, to_x, to_y, grid, party, start_node=False):
        if (to_x, to_y) not in grid:
            return False
        from_tile = grid.get((from_x, from_y))
        to_tile = grid.get((to_x, to_y))
        from_type = from_tile['type'] if from_tile else 'ground'
        to_type = to_tile['type']

        if to_type == 'impassable':
            return False

        if to_tile['has_object'] and not to_tile['is_warp']:
            if to_tile.get('object_id_name') != 'PIKACHU':
                return False

        if (from_type == 'water' and to_type == 'elevated_ground') or (from_type == 'elevated_ground' and to_type == 'water'):
            return False

        if {from_type, to_type} == {'ground', 'elevated_ground'}:
            return False

        general_traversable = [
            'ground', 'grass', 'cleared_boulder_barrier', 'open_gate', 'gate_offscreen',
            'spinner_stop', 'teleport', 'hole', 'ladder_up', 'ladder_down', 'elevated_ground', 'steps'
        ]
        general_traversable.extend(['spinner_up', 'spinner_down', 'spinner_left', 'spinner_right'])

        if from_type == 'water':
            return to_type == 'water' or to_type in ['ground', 'steps']

        if to_type == 'water':
            return False # Cannot walk from land to water. Requires Surf action.

        if to_type in general_traversable:
            return True

        if to_type == 'cuttable' and has_hm(party, 'CUT'):
            return True
        if to_type == 'ledge':
            return from_x == to_x and from_y == to_y - 1

        return False

    def get_neighbors(node, grid, party, map_width, map_height):
        x, y = node
        neighbors = []
        for dx, dy in [(0, 1), (0, -1), (1, 0), (-1, 0)]:
            nx, ny = x + dx, y + dy
            if 1 <= nx <= map_width and 1 <= ny <= map_height:
                if is_traversable(x, y, nx, ny, grid, party):
                    cost = 1
                    to_tile_type = grid.get((nx, ny), {}).get('type')
                    if to_tile_type == 'ledge' and dy == 1:
                        if is_traversable(nx, ny, nx, ny + 1, grid, party):
                           neighbors.append(((nx, ny + 1), cost))
                        continue 
                    neighbors.append(((nx, ny), cost))
        return neighbors

    def heuristic(a, b):
        return abs(a[0] - b[0]) + abs(a[1] - b[1])

    def a_star(start, goal, grid, party, map_width, map_height):
        if not is_traversable(goal[0], goal[1], goal[0], goal[1], grid, party, start_node=True):
            found_new_goal = False
            for dx, dy in [(0, 1), (0, -1), (1, 0), (-1, 0)]:
                new_goal_x, new_goal_y = goal[0] + dx, goal[1] + dy
                if is_traversable(new_goal_x, new_goal_y, new_goal_x, new_goal_y, grid, party):
                    goal = (new_goal_x, new_goal_y)
                    found_new_goal = True
                    break
            if not found_new_goal:
                return None

        frontier = [(0, start)]
        came_from = {start: None}
        cost_so_far = {start: 0}

        while frontier:
            _, current = heapq.heappop(frontier)
            if current == goal:
                break
            for next_node, cost in get_neighbors(current, grid, party, map_width, map_height):
                new_cost = cost_so_far[current] + cost
                if next_node not in cost_so_far or new_cost < cost_so_far[next_node]:
                    cost_so_far[next_node] = new_cost
                    priority = new_cost + heuristic(goal, next_node)
                    heapq.heappush(frontier, (priority, next_node))
                    came_from[next_node] = current
        else:
            return None

        path = []
        current = goal
        while current is not None:
            path.append(current)
            current = came_from.get(current)
        path.reverse()
        return path

    try:
        target_x = int(input_data['target_x'])
        target_y = int(input_data['target_y'])
        party = json.loads(input_data['party_data'])
        grid, player_pos, map_width, map_height = parse_map(map_xml_string)
        if player_pos is None:
            print(json.dumps({'error': 'Player position not found on the map.'}))
            return
        path_plan = a_star(player_pos, (target_x, target_y), grid, party, map_width, map_height)
        if path_plan:
            path_json = [{'x': x, 'y': y} for x, y in path_plan]
            print(json.dumps({'path_plan': path_json}))
        else:
            print(json.dumps({'error': 'No path found to the target.'}))
    except Exception as e:
        print(json.dumps({'error': f'An error occurred: {str(e)}'}))

spinner_maze_solver.python_script:
import xml.etree.ElementTree as ET
import heapq
import json

def spinner_maze_solver(input_data, map_xml_string):
    try:
        target_x = int(input_data['target_x'])
        target_y = int(input_data['target_y'])
        root = ET.fromstring(map_xml_string)
    except (ValueError, ET.ParseError):
        print(json.dumps({'error': 'Invalid input or map data.'}))
        return

    tile_map = {}
    player_pos = None
    width = int(root.get('width'))
    height = int(root.get('height'))

    for row_elem in root.findall('Row'):
        y = int(row_elem.get('id'))
        for tile_elem in row_elem.findall('Tile'):
            x = int(tile_elem.get('id'))
            tile_type = tile_elem.get('type')
            has_obj = tile_elem.find('Object') is not None and tile_elem.find('Object').get('id-name') != 'PIKACHU'
            spinner_info = tile_elem.find('Spinner')
            spinner_direction = spinner_info.get('direction') if spinner_info is not None else None
            tile_map[(x, y)] = {'type': tile_type, 'has_obj': has_obj, 'spinner': spinner_direction}
            if tile_elem.find('Player') is not None:
                player_pos = (x, y)

    if not player_pos:
        print(json.dumps({'error': 'Player position not found.'}))
        return

    def is_walkable(coord):
        if coord not in tile_map: return False
        tile = tile_map[coord]
        if tile['has_obj']: return False
        return tile['type'] not in ['impassable', 'boulder_barrier', 'unknown', 'closed_gate']

    def get_spinner_destination(start_coord):
        x, y = start_coord
        path = [start_coord]
        visited = {start_coord}
        while tile_map.get((x,y), {}).get('spinner') and tile_map[(x,y)]['spinner'] != 'stop':
            direction = tile_map[(x, y)]['spinner']
            if direction == 'up': y -= 1
            elif direction == 'down': y += 1
            elif direction == 'left': x -= 1
            elif direction == 'right': x += 1
            if not (1 <= x <= width and 1 <= y <= height) or (x, y) in visited:
                return None, [] # Loop or out of bounds
            visited.add((x, y))
            path.append((x, y))
        return (x, y), path

    target_node = (target_x, target_y)
    if not is_walkable(target_node):
        # Find adjacent walkable tile if target is not
        found_new_target = False
        for dx, dy in [(0, -1), (0, 1), (-1, 0), (1, 0)]:
            adj_target = (target_x + dx, target_y + dy)
            if is_walkable(adj_target):
                target_node = adj_target
                found_new_target = True
                break
        if not found_new_target:
            print(json.dumps({'error': 'Target is impassable and no adjacent traversable tile found.'}))
            return

    open_set = [(0, player_pos)]
    came_from = {}
    g_score = {player_pos: 0}

    def heuristic(a, b):
        return abs(a[0] - b[0]) + abs(a[1] - b[1])

    while open_set:
        _, current = heapq.heappop(open_set)

        if current == target_node:
            path = []
            while current in came_from:
                path.append(current)
                current = came_from[current]
            path.append(player_pos)
            path.reverse()

            final_path = []
            for i in range(len(path) - 1):
                start_node = path[i]
                final_path.append(start_node)
                if tile_map[start_node]['spinner']:
                    _, spin_path = get_spinner_destination(start_node)
                    if spin_path and len(spin_path) > 1:
                        final_path.extend(spin_path[1:])
            if path[-1] not in final_path:
                final_path.append(path[-1])

            print(json.dumps([{'x': x, 'y': y} for x, y in final_path]))
            return

        for dx, dy in [(0, 1), (0, -1), (1, 0), (-1, 0)]:
            neighbor = (current[0] + dx, current[1] + dy)
            if not is_walkable(neighbor): continue

            move_cost = 1
            final_dest = neighbor
            if tile_map[neighbor]['spinner']:
                dest, spin_path = get_spinner_destination(neighbor)
                if dest is None: continue # Spinner leads to loop/invalid state
                final_dest = dest
                move_cost = len(spin_path)

            tentative_g_score = g_score[current] + move_cost
            if final_dest not in g_score or tentative_g_score < g_score[final_dest]:
                g_score[final_dest] = tentative_g_score
                f_score = tentative_g_score + heuristic(final_dest, target_node)
                heapq.heappush(open_set, (f_score, final_dest))
                came_from[final_dest] = current

    print(json.dumps({'error': 'No path found.'}))

spinner_maze_solver(input_data, map_xml_string)
