import json
import xml.etree.ElementTree as ET
import heapq

def find_path(root, start, end):
    width = int(root.get('width'))
    height = int(root.get('height'))

    tile_types = {}
    objects = set()
    for r_idx, row_elem in enumerate(root.findall('Row')):
        y = int(row_elem.get('id'))
        for c_idx, tile_elem in enumerate(row_elem.findall('Tile')):
            x = int(tile_elem.get('id'))
            coord = (y, x)
            tile_types[coord] = tile_elem.get('type')
            if tile_elem.find('Object') is not None and tile_elem.find('Object').get('id-name') != 'Pikachu':
                objects.add(coord)

    open_set = [(0, start)]
    came_from = {}
    g_score = {start: 0}
    f_score = {start: abs(start[0] - end[0]) + abs(start[1] - end[1])}

    while open_set:
        _, current = heapq.heappop(open_set)

        if current == end:
            path = []
            while current in came_from:
                path.append(current)
                current = came_from[current]
            path.append(start)
            return path[::-1]

        for dr, dc, move_dir in [(1, 0, 'Down'), (-1, 0, 'Up'), (0, 1, 'Right'), (0, -1, 'Left')]:
            neighbor = (current[0] + dr, current[1] + dc)

            if not (1 <= neighbor[0] <= height and 1 <= neighbor[1] <= width):
                continue

            neighbor_type = tile_types.get(neighbor, 'unknown')
            if neighbor_type in ['impassable', 'unknown', 'closed_gate'] or neighbor in objects:
                continue

            current_type = tile_types.get(current, 'unknown')
            if current_type == 'ledge' and move_dir != 'Down':
                continue

            if neighbor_type == 'ledge' and move_dir != 'Down':
                continue

            final_neighbor = neighbor
            if neighbor_type == 'ledge' and move_dir == 'Down':
                final_neighbor = (neighbor[0] + 1, neighbor[1])
                if not (1 <= final_neighbor[0] <= height):
                    continue
                landing_type = tile_types.get(final_neighbor, 'unknown')
                if landing_type in ['impassable', 'unknown', 'closed_gate'] or final_neighbor in objects:
                    continue

            tentative_g_score = g_score[current] + 1
            if tentative_g_score < g_score.get(final_neighbor, float('inf')):
                came_from[final_neighbor] = current
                g_score[final_neighbor] = tentative_g_score
                f_score[final_neighbor] = tentative_g_score + abs(final_neighbor[0] - end[0]) + abs(final_neighbor[1] - end[1])
                heapq.heappush(open_set, (f_score[final_neighbor], final_neighbor))

    return None

