import xml.etree.ElementTree as ET
import heapq
import json

def heuristic(a, b):
    return abs(a[0] - b[0]) + abs(a[1] - b[1])

def get_neighbors(node, grid, width, height):
    x, y, mode = node
    neighbors = []
    walkable_types = {'ground', 'grass', 'steps', 'elevated_ground', 'cleared_boulder_barrier', 'boulder_switch', 'ladder_up', 'ladder_down'}
    surfable_types = {'water'}
    impassable_types = {'impassable', 'unknown', 'boulder_barrier'}

    # Movement in current mode
    for dx, dy in [(0, 1), (0, -1), (1, 0), (-1, 0)]:
        nx, ny = x + dx, y + dy
        if not (1 <= nx <= width and 1 <= ny <= height):
            continue

        neighbor_tile = grid[ny - 1][nx - 1]
        if neighbor_tile['type'] in impassable_types or neighbor_tile['has_impassable_object'] or neighbor_tile['has_boulder']:
            continue

        if mode == 'walking' and neighbor_tile['type'] in walkable_types:
            neighbors.append((nx, ny, 'walking'))
        elif mode == 'surfing' and neighbor_tile['type'] in surfable_types:
            neighbors.append((nx, ny, 'surfing'))

    # Mode transitions
    current_tile = grid[y - 1][x - 1]
    if mode == 'walking' and current_tile['type'] in walkable_types:
        for dx, dy in [(0, 1), (0, -1), (1, 0), (-1, 0)]:
            nx, ny = x + dx, y + dy
            if 1 <= nx <= width and 1 <= ny <= height:
                adj_tile = grid[ny - 1][nx - 1]
                if adj_tile['type'] in surfable_types:
                    neighbors.append((nx, ny, 'surfing'))
    elif mode == 'surfing' and current_tile['type'] in surfable_types:
        for dx, dy in [(0, 1), (0, -1), (1, 0), (-1, 0)]:
            nx, ny = x + dx, y + dy
            if 1 <= nx <= width and 1 <= ny <= height:
                adj_tile = grid[ny - 1][nx - 1]
                if adj_tile['type'] in walkable_types and not adj_tile['has_impassable_object'] and not adj_tile['has_boulder']:
                    neighbors.append((nx, ny, 'walking'))

    return neighbors

def a_star_search(start, end, grid, width, height, initial_mode):
    start_x, start_y = start
    end_x, end_y = end

    start_node = (start_x, start_y, initial_mode)

    open_set = []
    heapq.heappush(open_set, (0, start_node))
    came_from = {}
    g_score = {start_node: 0}
    f_score = {start_node: heuristic(start, end)}

    while open_set:
        _, current = heapq.heappop(open_set)
        current_pos = (current[0], current[1])

        if current_pos == end:
            path = []
            while current in came_from:
                path.append({'x': current[0], 'y': current[1]})
                current = came_from[current]
            path.append({'x': start_x, 'y': start_y})
            return path[::-1]

        for neighbor in get_neighbors(current, grid, width, height):
            tentative_g_score = g_score[current] + 1

            if tentative_g_score < g_score.get(neighbor, float('inf')):
                came_from[neighbor] = current
                g_score[neighbor] = tentative_g_score
                neighbor_pos = (neighbor[0], neighbor[1])
                f_score[neighbor] = tentative_g_score + heuristic(neighbor_pos, end)
                if neighbor not in [i[1] for i in open_set]:
                    heapq.heappush(open_set, (f_score[neighbor], neighbor))

    # Fallback to adjacent tile if destination is blocked
    for dx, dy in [(0, 1), (0, -1), (1, 0), (-1, 0)]:
        adj_x, adj_y = end_x + dx, end_y + dy
        if 1 <= adj_x <= width and 1 <= adj_y <= height:
            adj_tile = grid[adj_y - 1][adj_x - 1]
            if adj_tile['type'] not in {'impassable', 'unknown', 'boulder_barrier'} and not adj_tile['has_impassable_object'] and not adj_tile['has_boulder']:
                path_to_adj = a_star_search_single_mode(start, (adj_x, adj_y), grid, width, height, initial_mode)
                if path_to_adj:
                    return path_to_adj
    return None

def a_star_search_single_mode(start, end, grid, width, height, mode):
    # Simplified A* for fallback, assuming one mode
    open_set = []
    heapq.heappush(open_set, (0, start))
    came_from = {}
    g_score = {start: 0}
    f_score = {start: heuristic(start, end)}

    while open_set:
        _, current = heapq.heappop(open_set)
        if current == end:
            path = []
            while current in came_from:
                path.append({'x': current[0], 'y': current[1]})
                current = came_from[current]
            path.append({'x': start[0], 'y': start[1]})
            return path[::-1]
        # Simplified get_neighbors logic for single mode
        x,y = current
        for dx, dy in [(0, 1), (0, -1), (1, 0), (-1, 0)]:
            nx, ny = x + dx, y + dy
            if 1 <= nx <= width and 1 <= ny <= height:
                neighbor_tile = grid[ny - 1][nx - 1]
                if neighbor_tile['type'] not in {'impassable', 'unknown', 'boulder_barrier'} and not neighbor_tile['has_impassable_object'] and not neighbor_tile['has_boulder']:
                    if (mode == 'walking' and neighbor_tile['type'] in {'ground', 'grass'}) or (mode == 'surfing' and neighbor_tile['type'] == 'water'):
                        tentative_g_score = g_score[current] + 1
                        if tentative_g_score < g_score.get((nx,ny), float('inf')):
                            came_from[(nx,ny)] = current
                            g_score[(nx,ny)] = tentative_g_score
                            f_score[(nx,ny)] = tentative_g_score + heuristic((nx,ny), end)
                            heapq.heappush(open_set, (f_score[(nx,ny)], (nx,ny)))
    return None

try:
    start_x = int(input_data['start_x'])
    start_y = int(input_data['start_y'])
    end_x = int(input_data['end_x'])
    end_y = int(input_data['end_y'])

    tree = ET.fromstring(map_xml_string)
    width = int(tree.get('width'))
    height = int(tree.get('height'))

    grid = []
    for y, row_elem in enumerate(tree.findall('Row')):
        row = []
        for x, tile_elem in enumerate(row_elem.findall('Tile')):
            tile_type = tile_elem.get('type')
            obj_elem = tile_elem.find('Object')
            has_impassable_object = False
            if obj_elem is not None:
                obj_id_name = obj_elem.get('id-name', '')
                if obj_id_name not in ['Pikachu', 'Unknown']:
                    has_impassable_object = True
            has_boulder = tile_elem.find('Boulder') is not None
            row.append({'type': tile_type, 'has_impassable_object': has_impassable_object, 'has_boulder': has_boulder})
        grid.append(row)

    start_tile_type = grid[start_y - 1][start_x - 1]['type']
    initial_mode = 'surfing' if start_tile_type == 'water' else 'walking'

    start_node = (start_x, start_y)
    end_node = (end_x, end_y)

    path = a_star_search(start_node, end_node, grid, width, height, initial_mode)

    if path:
        print(json.dumps(path))
    else:
        print(json.dumps({"error": "No path found"}))

except Exception as e:
    print(json.dumps({"error": str(e)}))

generate_path_plan.python_script:
import xml.etree.ElementTree as ET
import json
import heapq

def get_neighbors(x, y, grid, width, height, assume_barriers_open):
    neighbors = []
    current_tile = grid[y - 1][x - 1]
    current_type = current_tile['type']

    for dx, dy in [(0, -1), (0, 1), (-1, 0), (1, 0)]:
        nx, ny = x + dx, y + dy

        if not (1 <= nx <= width and 1 <= ny <= height):
            continue

        neighbor_tile = grid[ny - 1][nx - 1]
        neighbor_type = neighbor_tile['type']

        # Basic impassable check
        impassable_types = {'impassable', 'unknown'}
        if neighbor_type in impassable_types or neighbor_tile['has_impassable_object'] or neighbor_tile['has_boulder']:
            continue
        if neighbor_type == 'boulder_barrier' and not assume_barriers_open:
            continue

        # Movement logic
        can_move = False
        # Ground to Ground/Special
        if current_type in ['ground', 'grass', 'boulder_switch', 'cleared_boulder_barrier']:
            if neighbor_type in ['ground', 'grass', 'boulder_switch', 'cleared_boulder_barrier']:
                can_move = True
            elif neighbor_type == 'steps':
                can_move = True
            elif neighbor_type == 'ledge' and ny > y:
                can_move = True # Can only jump down a ledge
        # Elevated to Elevated/Special
        elif current_type == 'elevated_ground':
            if neighbor_type == 'elevated_ground':
                can_move = True
            elif neighbor_type == 'steps':
                can_move = True
            elif neighbor_type in ['ground', 'grass', 'cleared_boulder_barrier'] and ny > y:
                 can_move = True # Drop down from elevation
        # Steps to Ground/Elevated
        elif current_type == 'steps':
            if neighbor_type in ['ground', 'grass', 'elevated_ground', 'boulder_switch', 'cleared_boulder_barrier']:
                can_move = True
        # Ledge to Ground (one way)
        elif current_type == 'ledge':
            if neighbor_type in ['ground', 'grass', 'cleared_boulder_barrier'] and ny > y:
                can_move = True

        if can_move:
            neighbors.append((nx, ny))

    return list(set(neighbors))

def heuristic(a, b):
    return abs(a[0] - b[0]) + abs(a[1] - b[1])

try:
    tree = ET.fromstring(map_xml_string)
    width = int(tree.get('width'))
    height = int(tree.get('height'))

    start_x, start_y = int(input_data['start_x']), int(input_data['start_y'])
    end_x, end_y = int(input_data['end_x']), int(input_data['end_y'])
    ignore_ids_str = input_data.get('ignore_object_ids', '')
    ignore_ids = [int(i.strip()) for i in ignore_ids_str.split(',') if i.strip().isdigit()] if ignore_ids_str else []
    assume_barriers_open = bool(input_data.get('assume_barriers_open', False))

    grid = []
    for y_idx, row_elem in enumerate(tree.findall('Row')):
        row = []
        for x_idx, tile_elem in enumerate(row_elem.findall('Tile')):
            tile_data = {
                'type': tile_elem.get('type'),
                'has_impassable_object': False,
                'has_boulder': False
            }
            obj_elem = tile_elem.find('Object')
            if obj_elem is not None and obj_elem.get('id-name', '') not in ['Pikachu', 'Unknown']:
                obj_id = obj_elem.get('id')
                if obj_id and int(obj_id) not in ignore_ids:
                    tile_data['has_impassable_object'] = True
            if tile_elem.find('Boulder') is not None:
                 tile_data['has_boulder'] = True
            row.append(tile_data)
        grid.append(row)

    open_set = []
    heapq.heappush(open_set, (0, (start_x, start_y)))
    came_from = {}
    g_score = {(x, y): float('inf') for x in range(1, width + 1) for y in range(1, height + 1)}
    g_score[(start_x, start_y)] = 0
    f_score = {(x, y): float('inf') for x in range(1, width + 1) for y in range(1, height + 1)}
    f_score[(start_x, start_y)] = heuristic((start_x, start_y), (end_x, end_y))

    path_found = False
    while open_set:
        _, current = heapq.heappop(open_set)

        if current == (end_x, end_y):
            path_found = True
            break

        for neighbor in get_neighbors(current[0], current[1], grid, width, height, assume_barriers_open):
            tentative_g_score = g_score[current] + 1
            if tentative_g_score < g_score[neighbor]:
                came_from[neighbor] = current
                g_score[neighbor] = tentative_g_score
                f_score[neighbor] = g_score[neighbor] + heuristic(neighbor, (end_x, end_y))
                if neighbor not in [i[1] for i in open_set]:
                    heapq.heappush(open_set, (f_score[neighbor], neighbor))

    if path_found:
        path = []
        temp = (end_x, end_y)
        while temp in came_from:
            path.append({'x': temp[0], 'y': temp[1]})
            temp = came_from[temp]
        path.append({'x': start_x, 'y': start_y})
        print(json.dumps({'path_found': True, 'path': path[::-1]}))
    else:
        print(json.dumps({'path_found': False, 'reason': 'No path could be found.'}))

except Exception as e:
    print(json.dumps({'path_found': False, 'reason': f'An error occurred: {str(e)}'}))

landmass_analyzer.python_script:
import xml.etree.ElementTree as ET
import json
from collections import deque

def get_neighbors(x, y, grid, width, height, assume_barriers_open):
    neighbors = []
    current_tile = grid[y - 1][x - 1]
    current_type = current_tile['type']

    potential_neighbors = [(x, y - 1), (x, y + 1), (x - 1, y), (x + 1, y)]

    for nx, ny in potential_neighbors:
        if not (1 <= nx <= width and 1 <= ny <= height):
            continue

        neighbor_tile = grid[ny - 1][nx - 1]
        neighbor_type = neighbor_tile['type']

        traversable_types = {'ground', 'grass', 'steps', 'elevated_ground', 'boulder_switch', 'cleared_boulder_barrier', 'ledge'}
        if assume_barriers_open:
            traversable_types.add('boulder_barrier')

        is_impassable = neighbor_type in {'impassable', 'unknown'} or neighbor_tile['has_impassable_object']
        if neighbor_type == 'boulder_barrier' and not assume_barriers_open:
            is_impassable = True

        if not is_impassable:
            neighbors.append((nx, ny))

    return list(set(neighbors))

try:
    tree = ET.fromstring(map_xml_string)
    width = int(tree.get('width'))
    height = int(tree.get('height'))

    start_x = int(input_data.get('start_x')) if input_data.get('start_x') else None
    start_y = int(input_data.get('start_y')) if input_data.get('start_y') else None
    end_x = int(input_data.get('end_x')) if input_data.get('end_x') else None
    end_y = int(input_data.get('end_y')) if input_data.get('end_y') else None
    ignore_ids_str = input_data.get('ignore_object_ids', '')
    ignore_ids = [int(i.strip()) for i in ignore_ids_str.split(',') if i.strip().isdigit()] if ignore_ids_str else []
    assume_barriers_open = bool(input_data.get('assume_barriers_open', False))

    grid = []
    for y_idx, row_elem in enumerate(tree.findall('Row')):
        row = []
        for x_idx, tile_elem in enumerate(row_elem.findall('Tile')):
            tile_data = {
                'type': tile_elem.get('type'),
                'has_impassable_object': False
            }
            obj_elem = tile_elem.find('Object')
            if obj_elem is not None and obj_elem.get('id-name', '') not in ['Pikachu', 'Unknown']:
                obj_id = obj_elem.get('id')
                if obj_id and int(obj_id) not in ignore_ids:
                    tile_data['has_impassable_object'] = True
            row.append(tile_data)
        grid.append(row)

    visited = set()
    landmasses = []
    landmass_id_counter = 1

    for y in range(1, height + 1):
        for x in range(1, width + 1):
            if (x, y) not in visited:
                tile = grid[y - 1][x - 1]
                tile_type = tile['type']

                is_impassable = tile_type in {'impassable', 'unknown'} or tile['has_impassable_object']
                if tile_type == 'boulder_barrier' and not assume_barriers_open:
                    is_impassable = True

                if not is_impassable:
                    current_landmass = {'id': landmass_id_counter, 'type': 'land', 'coordinates': []}
                    q = deque([(x, y)])
                    visited.add((x, y))

                    while q:
                        cx, cy = q.popleft()
                        current_landmass['coordinates'].append((cx, cy))

                        for nx, ny in get_neighbors(cx, cy, grid, width, height, assume_barriers_open):
                            if (nx, ny) not in visited:
                                visited.add((nx, ny))
                                q.append((nx, ny))

                    landmasses.append(current_landmass)
                    landmass_id_counter += 1

    result = {'landmasses': landmasses}
    if start_x and start_y:
        for lm in landmasses:
            if (start_x, start_y) in lm['coordinates']:
                result['start_point_landmass'] = lm['id']
                break
    if end_x and end_y:
        for lm in landmasses:
            if (end_x, end_y) in lm['coordinates']:
                result['end_point_landmass'] = lm['id']
                break

    print(json.dumps(result))

except Exception as e:
    print(json.dumps({'error': str(e), 'line': e.__traceback__.tb_lineno}))

boulder_puzzle_assistant.python_script:
import xml.etree.ElementTree as ET
import json

def is_traversable(x, y, grid, width, height):
    if not (1 <= x <= width and 1 <= y <= height):
        return False
    tile = grid[y - 1][x - 1]
    impassable_types = {'impassable', 'unknown', 'boulder_barrier'}
    if tile['type'] in impassable_types or tile['has_impassable_object'] or tile['has_boulder']:
        return False
    return True

try:
    tree = ET.fromstring(map_xml_string)
    width = int(tree.get('width'))
    height = int(tree.get('height'))
    player_pos = None

    grid = []
    boulders = []
    for y_idx, row_elem in enumerate(tree.findall('Row')):
        row = []
        for x_idx, tile_elem in enumerate(row_elem.findall('Tile')):
            x, y = x_idx + 1, y_idx + 1
            tile_data = {
                'type': tile_elem.get('type'),
                'has_impassable_object': False,
                'has_boulder': False
            }
            if tile_elem.find('Player') is not None:
                player_pos = (x, y)
            if tile_elem.find('Boulder') is not None:
                tile_data['has_boulder'] = True
                boulders.append({'id': tile_elem.find('Boulder').get('id-name', f'boulder_{len(boulders)+1}'), 'pos': (x, y)})
            obj_elem = tile_elem.find('Object')
            if obj_elem is not None and obj_elem.get('id-name', '') not in ['Pikachu', 'Unknown']:
                tile_data['has_impassable_object'] = True
            row.append(tile_data)
        grid.append(row)

    possible_moves = []
    for boulder in boulders:
        bx, by = boulder['pos']
        boulder_moves = {'id': boulder['id'], 'pos': boulder['pos'], 'moves': []}

        # Check Up
        if is_traversable(bx, by - 1, grid, width, height):
            if is_traversable(bx, by + 1, grid, width, height):
                boulder_moves['moves'].append({'direction': 'Up', 'player_pos': (bx, by + 1)})
            if is_traversable(bx, by + 2, grid, width, height):
                boulder_moves['moves'].append({'direction': 'Up', 'player_pos': (bx, by + 2), 'type': 'remote'})

        # Check Down
        if is_traversable(bx, by + 1, grid, width, height):
            if is_traversable(bx, by - 1, grid, width, height):
                boulder_moves['moves'].append({'direction': 'Down', 'player_pos': (bx, by - 1)})
            if is_traversable(bx, by - 2, grid, width, height):
                boulder_moves['moves'].append({'direction': 'Down', 'player_pos': (bx, by - 2), 'type': 'remote'})

        # Check Left
        if is_traversable(bx - 1, by, grid, width, height):
            if is_traversable(bx + 1, by, grid, width, height):
                boulder_moves['moves'].append({'direction': 'Left', 'player_pos': (bx + 1, by)})
            if is_traversable(bx + 2, by, grid, width, height):
                boulder_moves['moves'].append({'direction': 'Left', 'player_pos': (bx + 2, by), 'type': 'remote'})

        # Check Right
        if is_traversable(bx + 1, by, grid, width, height):
            if is_traversable(bx - 1, by, grid, width, height):
                boulder_moves['moves'].append({'direction': 'Right', 'player_pos': (bx - 1, by)})
            if is_traversable(bx - 2, by, grid, width, height):
                boulder_moves['moves'].append({'direction': 'Right', 'player_pos': (bx - 2, by), 'type': 'remote'})

        if boulder_moves['moves']:
            possible_moves.append(boulder_moves)

    print(json.dumps(possible_moves))
except Exception as e:
    print(json.dumps({'error': str(e)}))
