import xml.etree.ElementTree as ET
import heapq

map_xml_string = globals().get("map_xml_string")
root = ET.fromstring(map_xml_string)
map_width = int(root.attrib['width'])
map_height = int(root.attrib['height'])

start_x = int(input_data['start_x'])
start_y = int(input_data['start_y'])
end_x = int(input_data['end_x'])
end_y = int(input_data['end_y'])
start_node = (start_x, start_y)
end_node = (end_x, end_y)
original_end_node = end_node

grid = [['UNSEEN' for _ in range(map_width + 1)] for _ in range(map_height + 1)]
obstacles = set()

for row in root.findall('Row'):
    y = int(row.attrib['id'])
    for tile in row.findall('Tile'):
        x = int(tile.attrib['id'])
        if tile.attrib['seen'] == 'true':
            grid[y][x] = tile.attrib['type']
            if tile.attrib.get('has-object') == 'true':
                obstacles.add((x, y))
            for marker in tile.findall('Marker'):
                if marker.attrib['emoji'] == '☠️':
                    obstacles.add((x, y))

impassable_types = {
    'WALL', 'VOID', 'PILLAR', 'COUNTER', 'MART_SHELF', 'BOOKSHELF', 'TV', 
    'RADIO', 'PC', 'TOWN_MAP', 'WINDOW', 'PAINTING', 'CUT_TREE', 'WATER', 
    'BUOY', 'STATUE', 'HEADBUTT_TREE'
}

def is_generally_impassable(coord):
    x, y = coord
    if not (1 <= x <= map_width and 1 <= y <= map_height):
        return True
    tile_type = grid[y][x]
    if tile_type == 'UNSEEN':
        return True
    if tile_type in impassable_types:
        return True
    if coord in obstacles:
        return True
    return False

if is_generally_impassable(start_node):
    print(f"Error: Start node {start_node} is on an impassable tile.")
    exit()

if is_generally_impassable(end_node):
    print(f"Info: Original destination {original_end_node} is impassable. Searching for a valid adjacent tile...")
    found_new_target = False
    for dx, dy in [(0, 1), (0, -1), (1, 0), (-1, 0)]:
        new_end_node = (original_end_node[0] + dx, original_end_node[1] + dy)
        if not is_generally_impassable(new_end_node):
            end_node = new_end_node
            print(f"Info: New destination set to {end_node}.")
            found_new_target = True
            break
    if not found_new_target:
        print(f"Error: Could not find any traversable tiles adjacent to the original destination {original_end_node}.")
        exit()

def heuristic(a, b):
    return abs(a[0] - b[0]) + abs(a[1] - b[1])

open_set = []
heapq.heappush(open_set, (0, start_node))
came_from = {}
g_score = {start_node: 0}
f_score = {start_node: heuristic(start_node, end_node)}

path_found = False
while open_set:
    _, current = heapq.heappop(open_set)

    if current == end_node:
        path = []
        temp_current = current
        while temp_current in came_from:
            path.append(f"{temp_current[0]},{temp_current[1]}")
            temp_current = came_from[temp_current]
        path.append(f"{start_node[0]},{start_node[1]}")
        print(','.join(path[::-1]))
        path_found = True
        break

    for dx, dy in [(0, 1), (0, -1), (1, 0), (-1, 0)]:
        neighbor = (current[0] + dx, current[1] + dy)

        if is_generally_impassable(neighbor):
            continue

        # Source tile restrictions
        current_tile_type = grid[current[1]][current[0]]
        if 'FLOOR_HOP' in current_tile_type:
            if 'DOWN' in current_tile_type and dy != 1: continue
            elif 'UP' in current_tile_type and dy != -1: continue
            elif 'LEFT' in current_tile_type and dx != -1: continue
            elif 'RIGHT' in current_tile_type and dx != 1: continue

        # Destination tile restrictions
        neighbor_tile_type = grid[neighbor[1]][neighbor[0]]
        if 'FLOOR_HOP' in neighbor_tile_type: # Cannot move ONTO a hop tile
            continue
        if 'LEDGE' in neighbor_tile_type:
            if ('DOWN' in neighbor_tile_type or neighbor_tile_type == 'LEDGE') and dy != 1: continue
            elif 'UP' in neighbor_tile_type and dy != -1: continue
            elif 'LEFT' in neighbor_tile_type and dx != -1: continue
            elif 'RIGHT' in neighbor_tile_type and dx != 1: continue

        tentative_g_score = g_score.get(current, float('inf')) + 1
        if tentative_g_score < g_score.get(neighbor, float('inf')):
            came_from[neighbor] = current
            g_score[neighbor] = tentative_g_score
            f_score[neighbor] = tentative_g_score + heuristic(neighbor, end_node)
            if neighbor not in [i[1] for i in open_set]:
                heapq.heappush(open_set, (f_score[neighbor], neighbor))

if not path_found:
    print(f"Error: No path found. The destination {end_node} is likely in a disconnected area from the start {start_node}.")
