import collections
import xml.etree.ElementTree as ET

def pathfinder(input_data, map_xml_string):
    target_x = int(input_data['target_x'])
    target_y = int(input_data['target_y'])

    try:
        root = ET.fromstring(map_xml_string)
    except ET.ParseError:
        return 'Error: Could not parse map XML.'

    player_pos = None
    for row_elem in root.findall('Row'):
        for tile_elem in row_elem.findall('Tile'):
            if tile_elem.find('Player') is not None:
                player_pos = (int(tile_elem.get('id')), int(row_elem.get('id')))
                break
        if player_pos:
            break

    if not player_pos:
        return 'Error: Player position not found.'

    tile_map = {}
    for row_elem in root.findall('Row'):
        y = int(row_elem.get('id'))
        for tile_elem in row_elem.findall('Tile'):
            x = int(tile_elem.get('id'))
            tile_type = tile_elem.get('type')
            has_obj = tile_elem.find('Object') is not None
            obj_is_pikachu = False
            if has_obj:
                obj_elem = tile_elem.find('Object')
                if obj_elem.get('id-name') == 'Pikachu':
                    obj_is_pikachu = True
            tile_map[(x, y)] = {'type': tile_type, 'has_obj': has_obj, 'obj_is_pikachu': obj_is_pikachu}

    def is_walkable(x, y):
        if (x, y) not in tile_map: return False
        tile = tile_map[(x, y)]
        if tile['has_obj'] and not tile['obj_is_pikachu']: return False
        walkable_types = ['ground', 'grass', 'steps', 'cleared_boulder_barrier', 'open_gate', 'gate_offscreen', 'teleport', 'ladder_up', 'ladder_down', 'spinner_stop', 'spinner_up', 'spinner_down', 'spinner_left', 'spinner_right']
        return tile['type'] in walkable_types

    start_node = player_pos
    target_node = (target_x, target_y)
    is_target_impassable = not is_walkable(target_x, target_y)

    queue = collections.deque([[start_node]])
    seen = {start_node}

    while queue:
        path = queue.popleft()
        x, y = path[-1]

        if not is_target_impassable and (x, y) == target_node:
            return path
        if is_target_impassable and abs(x - target_x) + abs(y - target_y) == 1:
            return path

        for dx, dy in [(0, 1), (0, -1), (1, 0), (-1, 0)]:
            next_x, next_y = x + dx, y + dy
            neighbor = (next_x, next_y)

            if neighbor not in tile_map or neighbor in seen:
                continue

            current_tile_type = tile_map.get((x,y), {}).get('type')
            neighbor_tile_type = tile_map.get(neighbor, {}).get('type')

            final_neighbor = neighbor
            if current_tile_type == 'ground' and neighbor_tile_type == 'ledge' and dy == 1:
                final_neighbor = (next_x, next_y + 1)
                if final_neighbor not in tile_map or not is_walkable(final_neighbor[0], final_neighbor[1]):
                    continue
            elif not is_walkable(next_x, next_y):
                continue

            if final_neighbor in seen: continue

            new_path = list(path)
            new_path.append(final_neighbor)
            queue.append(new_path)
            seen.add(final_neighbor)

    return 'No path found.'

result = pathfinder(input_data, map_xml_string)
print(result)

