import heapq
import xml.etree.ElementTree as ET

def heuristic(a, b):
    return abs(a[0] - b[0]) + abs(a[1] - b[1])

def spinner_maze_solver(input_data, map_xml_string):
    try:
        target_x = int(input_data['target_x'])
        target_y = int(input_data['target_y'])
    except (ValueError, KeyError) as e:
        return f'Invalid input: {e}'

    try:
        root = ET.fromstring(map_xml_string)
    except ET.ParseError as e:
        return f'Error parsing map XML: {e}'

    player_pos = None
    for row_elem in root.findall('Row'):
        for tile_elem in row_elem.findall('Tile'):
            if tile_elem.find('Player') is not None:
                player_pos = (int(tile_elem.get('id')), int(row_elem.get('id')))
                break
        if player_pos:
            break

    if not player_pos:
        return 'Player position not found on the map.'

    start_node = player_pos
    target_node = (target_x, target_y)

    grid = {}
    for row_elem in root.findall('Row'):
        y = int(row_elem.get('id'))
        for tile_elem in row_elem.findall('Tile'):
            x = int(tile_elem.get('id'))
            tile_type = tile_elem.get('type')
            has_obj = tile_elem.find('Object') is not None
            obj_is_pikachu = False
            if has_obj:
                obj_elem = tile_elem.find('Object')
                obj_name = obj_elem.get('id-name')
                if obj_name and obj_name == 'Pikachu':
                    obj_is_pikachu = True

            is_impassable = tile_type == 'impassable' or (has_obj and not obj_is_pikachu)

            spinner_info = None
            if tile_elem.find('Spinner') is not None:
                spinner_elem = tile_elem.find('Spinner')
                direction = spinner_elem.get('direction')
                end_coord_str = spinner_elem.get('end-coordinate')
                end_coord = tuple(map(int, end_coord_str.strip('()').split(','))) if end_coord_str else None
                spinner_info = {'direction': direction, 'end': end_coord}

            grid[(x, y)] = {'impassable': is_impassable, 'spinner': spinner_info}

    open_set = [(heuristic(start_node, target_node), start_node)]
    came_from = {}
    g_score = {start_node: 0}

    is_target_impassable = grid.get(target_node, {}).get('impassable', True)

    while open_set:
        _, current = heapq.heappop(open_set)

        if not is_target_impassable and current == target_node:
            path = []
            while current in came_from:
                path.append(current)
                current = came_from[current]
            path.append(start_node)
            return path[::-1]

        if is_target_impassable and heuristic(current, target_node) == 1:
            if not grid.get(current, {}).get('impassable', True):
                path = []
                while current in came_from:
                    path.append(current)
                    current = came_from[current]
                path.append(start_node)
                return path[::-1]

        current_spinner = grid[current]['spinner']
        if current_spinner and current_spinner['end']:
            neighbor = current_spinner['end']
            if neighbor in grid and not grid[neighbor]['impassable']:
                tentative_g_score = g_score[current] + 1 
                if neighbor not in g_score or tentative_g_score < g_score[neighbor]:
                    came_from[neighbor] = current
                    g_score[neighbor] = tentative_g_score
                    f_score = tentative_g_score + heuristic(neighbor, target_node)
                    heapq.heappush(open_set, (f_score, neighbor))
            continue

        for dx, dy in [(0, 1), (0, -1), (1, 0), (-1, 0)]:
            neighbor = (current[0] + dx, current[1] + dy)
            if neighbor not in grid or grid[neighbor]['impassable']:
                continue

            tentative_g_score = g_score[current] + 1
            if neighbor not in g_score or tentative_g_score < g_score[neighbor]:
                came_from[neighbor] = current
                g_score[neighbor] = tentative_g_score
                f_score = tentative_g_score + heuristic(neighbor, target_node)
                heapq.heappush(open_set, (f_score, neighbor))

    return 'No path found.'

print(spinner_maze_solver(input_data, map_xml_string))
