import json
import collections
import xml.etree.ElementTree as ET

def get_path(start_x, start_y, end_x, end_y, map_xml, autopress_buttons):
    root = ET.fromstring(map_xml)
    width = int(root.get('width'))
    height = int(root.get('height'))

    grid = {}
    for row in root.findall('Row'):
        y = int(row.get('id'))
        for tile in row.findall('Tile'):
            x = int(tile.get('id'))
            t_type = tile.get('type')
            seen = tile.get('seen') == 'true'
            has_obj = tile.get('has-object') == 'true'
            grid[(x, y)] = {'type': t_type, 'seen': seen, 'has_obj': has_obj}

    queue = collections.deque([(start_x, start_y, [])])
    visited = {(start_x, start_y)}

    while queue:
        cx, cy, path = queue.popleft()
        if cx == end_x and cy == end_y:
            if autopress_buttons:
                buttons = []
                for i in range(len(path)):
                    curr = path[i-1] if i > 0 else (start_x, start_y)
                    nxt = path[i]
                    if nxt[0] > curr[0]: buttons.append("Right")
                    elif nxt[0] < curr[0]: buttons.append("Left")
                    elif nxt[1] > curr[1]: buttons.append("Down")
                    elif nxt[1] < curr[1]: buttons.append("Up")
                return buttons
            else:
                return [{"x": p[0], "y": p[1]} for p in [(start_x, start_y)] + path]

        for dx, dy in [(0, 1), (0, -1), (1, 0), (-1, 0)]:
            nx, ny = cx + dx, cy + dy
            if 0 <= nx < width and 0 <= ny < height:
                target = grid.get((nx, ny))
                if not target: continue

                # Treat unseen as impassable for reliable pathfinding
                if not target['seen'] and not (nx == end_x and ny == end_y):
                    continue

                # Basic collision
                if target['type'] in ['WALL', 'COUNTER', 'FLOOR_UP_WALL'] or target['has_obj']:
                    if not (nx == end_x and ny == end_y):
                        continue

                # Ledge logic (one-way)
                if target['type'] == 'LEDGE_HOP_DOWN':
                    if dy != 1: # Can only enter from above
                        continue

                if (nx, ny) not in visited:
                    visited.add((nx, ny))
                    queue.append((nx, ny, path + [(nx, ny)]))
    return []

