import xml.etree.ElementTree as ET
import json
from collections import deque
import heapq

def parse_map(map_xml_string):
    root = ET.fromstring(map_xml_string)
    grid = {}
    unseen_tiles = set()
    all_impassable_coords = set()
    impassable_marker_emojis = {'ðŸ“', 'ðŸš€', 'ðŸ‘¦', 'ðŸ‘©â€ðŸ«', 'ðŸ‘´', 'âš”ï¸', 'ðŸ’°', 'ðŸŽ£', 'ðŸ˜Ž', 'ðŸŽ', 'â“', 'ðŸ¤“'}
    for row_elem in root.findall('Row'):
        y = int(row_elem.get('id'))
        for tile_elem in row_elem.findall('Tile'):
            x = int(tile_elem.get('id'))
            tile_type = tile_elem.get('type')
            grid[(x, y)] = {'type': tile_type}
            if tile_elem.get('seen') == 'false':
                unseen_tiles.add((x, y))
            if tile_elem.find('Object') is not None:
                all_impassable_coords.add((x, y))
            for marker_elem in tile_elem.findall('Marker'):
                if marker_elem.get('emoji') in impassable_marker_emojis:
                    all_impassable_coords.add((x, y))
    return grid, unseen_tiles, all_impassable_coords

def get_neighbors_with_state(grid, pos, is_surfing_now):
    neighbors = []
    current_type = grid[pos]['type']
    traversable_land = {'FLOOR', 'LADDER', 'STAIRCASE', 'DOOR', 'CAVE', 'GRASS', 'TALL_GRASS', 'LONG_GRASS'}
    impassable_types = {
        'WALL', 'HEADBUTT_TREE', 'PILLAR', 'BOOKSHELF', 'TV', 'RADIO', 'TOWN_MAP', 'WINDOW', 'STATUE', 'TABLE', 'CHAIR', 'BIRD', 
        'MART_SHELF', 'BUOY', 'PC', 'LINK_CABLE', 'TRADE_MACHINE', 'INCENSE_BURNER', 'COUNTER', 'ROOF', 'CHIMNEY', 'SIGN', 
        'FLOWER', 'TREE_TOP', 'WATER_EDGE_UP', 'WATER_EDGE_DOWN', 'WATER_EDGE_LEFT', 'WATER_EDGE_RIGHT', 'VOID', 'PIT'
    }
    for dx, dy in [(0, -1), (0, 1), (-1, 0), (1, 0)]:
        next_pos = (pos[0] + dx, pos[1] + dy)
        if next_pos not in grid:
            continue
        next_type = grid[next_pos]['type']
        if current_type == 'FLOOR_UP_WALL' and dy == -1: continue
        if next_type == 'FLOOR_UP_WALL' and current_type not in ['FLOOR_UP_WALL'] and dy != -1: continue

        if next_type in impassable_types: continue

        if is_surfing_now:
            if next_type in traversable_land:
                neighbors.append((next_pos, False))
            elif next_type == 'WATER':
                neighbors.append((next_pos, True))
        else:
            if next_type == 'WATER':
                neighbors.append((next_pos, True))
            elif next_type in traversable_land or (current_type == 'FLOOR_UP_WALL' and next_type in traversable_land):
                neighbors.append((next_pos, False))
    return neighbors

def find_path_astar_stateful(grid, start, end, all_impassable, avoid_coords_set):
    start_type = grid.get(start, {}).get('type')
    start_is_surfing = (start_type == 'WATER')
    open_set = [(0, start, start_is_surfing)]
    came_from = {}
    g_score = {(start, start_is_surfing): 0}

    iteration = 0
    while open_set:
        iteration += 1
        if iteration > 10000: return None, 'Max iterations exceeded.'
        _, current_pos, is_surfing = heapq.heappop(open_set)
        if current_pos == end:
            path = []
            state = (current_pos, is_surfing)
            while state in came_from:
                path.append(state[0])
                state = came_from[state]
            path.append(start)
            return path[::-1], None

        for neighbor_pos, next_is_surfing in get_neighbors_with_state(grid, current_pos, is_surfing):
            if neighbor_pos in all_impassable or neighbor_pos in avoid_coords_set: continue
            tentative_g_score = g_score.get((current_pos, is_surfing), float('inf')) + 1
            if tentative_g_score < g_score.get((neighbor_pos, next_is_surfing), float('inf')):
                came_from[(neighbor_pos, next_is_surfing)] = (current_pos, is_surfing)
                g_score[(neighbor_pos, next_is_surfing)] = tentative_g_score
                f_score = tentative_g_score + abs(neighbor_pos[0] - end[0]) + abs(neighbor_pos[1] - end[1])
                heapq.heappush(open_set, (f_score, neighbor_pos, next_is_surfing))

    return None, f'No path found from {start} to {end}.'

try:
    start_x = int(input_data['start_x']); start_y = int(input_data['start_y'])
    avoid_coords_raw = json.loads(input_data.get('avoid_coords', '[]'))
    avoid_coords_set = set(tuple(c) for c in avoid_coords_raw) if avoid_coords_raw else set()
    grid, unseen_tiles, all_impassable = parse_map(map_xml_string)
    start_pos = (start_x, start_y)
    end_x_str = input_data.get('end_x'); end_y_str = input_data.get('end_y')
    end_pos = (int(end_x_str), int(end_y_str))
    path, error = find_path_astar_stateful(grid, start_pos, end_pos, all_impassable, avoid_coords_set)
    path_coords = [[p[0], p[1]] for p in path] if path else []
    print(json.dumps({'path': path_coords, 'error': error}))
except Exception as e:
    import traceback
    print(json.dumps({'path': [], 'error': f'An exception occurred: {str(e)}\n{traceback.format_exc()}'}))
