smart_battle_move.python_script:
# Smart Battle Move Tool (Clean Version)
import json

try:
    # Argument Handling Logic
    args = locals()
    # Default values
    t_slot = 1
    c_slot = 1

    # Extract target_slot from locals or input_data
    if 'target_slot' in args:
        t_slot = int(args['target_slot'])
    elif 'input_data' in args and isinstance(args['input_data'], dict) and 'target_slot' in args['input_data']:
        t_slot = int(args['input_data']['target_slot'])

    # Extract current_slot from locals or input_data
    if 'current_slot' in args:
        c_slot = int(args['current_slot'])
    elif 'input_data' in args and isinstance(args['input_data'], dict) and 'current_slot' in args['input_data']:
        c_slot = int(args['input_data']['current_slot'])

    buttons = ["A"] # Select FIGHT to enter menu

    if t_slot == c_slot:
        pass # No movement needed
    else:
        # Calculate distances in a 4-item vertical list (0-3 relative)
        # Down distance (forward)
        down_steps = (t_slot - c_slot) % 4
        # Up distance (backward)
        up_steps = (c_slot - t_slot) % 4

        if down_steps <= up_steps:
            for _ in range(down_steps):
                buttons.append("Down")
        else:
            for _ in range(up_steps):
                buttons.append("Up")

    buttons.append("A") # Select the move

    print(json.dumps(buttons))

except Exception as e:
    # Fallback to empty list to avoid crashing, handled by system
    print(json.dumps([]))

