import heapq
import xml.etree.ElementTree as ET
import json

def pathfinder(map_xml_string, start_x, start_y, end_x, end_y, traversable_tiles, party_pokemon_json, obstacles_json=None):
    root = ET.fromstring(map_xml_string)
    map_width = int(root.get('width'))
    map_height = int(root.get('height'))

    obstacles = set()
    if obstacles_json:
        try:
            user_obstacles = json.loads(obstacles_json)
            for obs in user_obstacles:
                obstacles.add((obs['x'], obs['y']))
        except (json.JSONDecodeError, KeyError):
            pass

    current_traversable = set(traversable_tiles)
    try:
        party = json.loads(party_pokemon_json)
        for pokemon in party:
            if any('SURF' in move.upper() for move in pokemon.get('moves', [])):
                current_traversable.update(['WATER', 'SEA'])
                break
    except (json.JSONDecodeError, KeyError):
        pass

    for row in root.findall('Row'):
        for tile in row.findall('Tile'):
            x = int(tile.get('id'))
            y = int(row.get('id'))
            tile_type = tile.get('type')

            if tile_type not in current_traversable:
                obstacles.add((x, y))

            if tile.find('Object') is not None:
                obstacles.add((x, y))

    def heuristic(a, b):
        return abs(a[0] - b[0]) + abs(a[1] - b[1])

    start = (start_x, start_y)
    goal = (end_x, end_y)

    if goal in obstacles:
        found_new_goal = False
        for dx, dy in [(0, 1), (0, -1), (1, 0), (-1, 0)]:
            new_goal = (goal[0] + dx, goal[1] + dy)
            if new_goal not in obstacles:
                goal = new_goal
                found_new_goal = True
                break
        if not found_new_goal:
            return "Error: Goal is surrounded by obstacles."

    open_set = []
    heapq.heappush(open_set, (0, start))
    came_from = {}
    g_score = { (x,y): float('inf') for y in range(map_height + 1) for x in range(map_width + 1) }
    g_score[start] = 0
    f_score = { (x,y): float('inf') for y in range(map_height + 1) for x in range(map_width + 1) }
    f_score[start] = heuristic(start, goal)

    max_iterations = (map_width * map_height) * 2
    iterations = 0

    while open_set and iterations < max_iterations:
        iterations += 1
        current = heapq.heappop(open_set)[1]

        if current == goal:
            path = []
            while current in came_from:
                prev = came_from[current]
                if current[0] > prev[0]: path.append("Right")
                elif current[0] < prev[0]: path.append("Left")
                elif current[1] > prev[1]: path.append("Down")
                elif current[1] < prev[1]: path.append("Up")
                current = prev
            return path[::-1]

        for dx, dy in [(0, 1), (0, -1), (1, 0), (-1, 0)]:
            neighbor = (current[0] + dx, current[1] + dy)

            if not (1 <= neighbor[0] <= map_width and 1 <= neighbor[1] <= map_height):
                continue

            if neighbor in obstacles:
                continue

            current_tile_elem = root.find(f".//Row[@id='{current[1]}']/Tile[@id='{current[0]}']")
            if current_tile_elem is not None and 'LEDGE' in current_tile_elem.get('type'):
                 if 'DOWN' in current_tile_elem.get('type') and dy != 1: continue
                 if 'LEFT' in current_tile_elem.get('type') and dx != -1: continue
                 if 'RIGHT' in current_tile_elem.get('type') and dx != 1: continue

            tentative_g_score = g_score[current] + 1
            if tentative_g_score < g_score.get(neighbor, float('inf')):
                came_from[neighbor] = current
                g_score[neighbor] = tentative_g_score
                f_score[neighbor] = g_score[neighbor] + heuristic(neighbor, goal)
                if neighbor not in [i[1] for i in open_set]:
                    heapq.heappush(open_set, (f_score[neighbor], neighbor))

    return "Error: No path found."

try:
    start_x_val = int(input_data['start_x'])
    start_y_val = int(input_data['start_y'])
    end_x_val = int(input_data['end_x'])
    end_y_val = int(input_data['end_y'])
    traversable_tiles_val = json.loads(input_data['traversable_tiles'])
    party_pokemon_json_val = input_data['party_pokemon_json']
    obstacles_json_val = input_data.get('obstacles_json')

    path_result = pathfinder(map_xml_string, start_x_val, start_y_val, end_x_val, end_y_val, traversable_tiles_val, party_pokemon_json_val, obstacles_json_val)
    print(json.dumps(path_result))
except Exception as e:
    print(json.dumps(f"Error executing pathfinder: {e}"))

