import xml.etree.ElementTree as ET
import json
from collections import deque

def parse_map(xml_string):
    root = ET.fromstring(xml_string)
    width = int(root.get('width'))
    height = int(root.get('height'))
    grid = [['' for _ in range(width + 1)] for _ in range(height + 1)]
    impassable_types = ['WALL', 'COUNTER', 'MART_SHELF', 'PC', 'BOOKSHELF', 'HEADBUTT_TREE', 'CUT_TREE', 'TV', 'TOWN_MAP', 'WINDOW', 'RADIO', 'ROCK', 'BUOY', 'VOID']

    for row_elem in root.findall('Row'):
        y = int(row_elem.get('id'))
        for tile_elem in row_elem.findall('Tile'):
            x = int(tile_elem.get('id'))
            tile_type = tile_elem.get('type')

            is_impassable = tile_type in impassable_types
            if tile_elem.find('Object') is not None:
                is_impassable = True

            grid[y][x] = '#' if is_impassable else '.'

    return grid, width, height

def solve_maze(grid, width, height, start_x, start_y, end_x, end_y, arrow_tiles):
    q = deque([((start_x, start_y), [])])
    visited = { (start_x, start_y) }

    while q:
        (current_x, current_y), path = q.popleft()

        if (current_x, current_y) == (end_x, end_y):
            return path

        moves = {'Up': (0, -1), 'Down': (0, 1), 'Left': (-1, 0), 'Right': (1, 0)}

        for move_name, (dx, dy) in moves.items():
            next_path = path + [move_name]
            sim_x, sim_y = current_x + dx, current_y + dy

            if not (0 <= sim_x <= width and 0 <= sim_y <= height and grid[sim_y][sim_x] == '.'):
                continue

            final_x, final_y = sim_x, sim_y
            while str((final_x, final_y)) in arrow_tiles:
                arrow_dir = arrow_tiles[str((final_x, final_y))]
                adx, ady = moves[arrow_dir]
                peek_x, peek_y = final_x + adx, final_y + ady
                if not (0 <= peek_x <= width and 0 <= peek_y <= height and grid[peek_y][peek_x] == '.'):
                    break
                final_x, final_y = peek_x, peek_y

            if (final_x, final_y) not in visited:
                visited.add((final_x, final_y))
                q.append(((final_x, final_y), next_path))

    return None

try:
    start_x = int(input_data['start_x'])
    start_y = int(input_data['start_y'])
    end_x = int(input_data['end_x'])
    end_y = int(input_data['end_y'])
    arrow_tiles_json = input_data['arrow_tiles_json']

    arrow_tiles = json.loads(arrow_tiles_json)
    parsed_arrow_tiles = {str(eval(k)): v for k, v in arrow_tiles.items()}

    grid, width, height = parse_map(map_xml_string)

    path = solve_maze(grid, width, height, start_x, start_y, end_x, end_y, parsed_arrow_tiles)

    if path:
        print(json.dumps({"status": "success", "path": path}))
    else:
        print(json.dumps({"status": "failure", "path": []}))

except Exception as e:
    print(json.dumps({"status": "error", "message": str(e)}))
