import xml.etree.ElementTree as ET
from collections import deque
import json

try:
    root = ET.fromstring(map_xml_string)

    width = int(root.get("width"))
    height = int(root.get("height"))

    grid = {}
    player_pos = None

    for row in root.findall("Row"):
        y = int(row.get("id"))
        for tile in row.findall("Tile"):
            x = int(tile.get("id"))
            t_type = tile.get("type")
            has_obj = tile.get("has-object") == "true"
            grid[(x,y)] = {"type": t_type, "has_object": has_obj}

            if tile.find("Player") is not None:
                player_pos = (x, y)

    if not player_pos:
        print(json.dumps([]))
        exit()

    start = player_pos
    goal = (target_x, target_y)

    if start == goal:
        print(json.dumps([]))
        exit()

    NON_WALKABLE_TYPES = {
        "WALL", "WATER", "HEADBUTT_TREE", "CUT_TREE", "TREE", "ROCK", "BOULDER", 
        "COUNTER", "PC", "SIGN", "WINDOW",
        "LEDGE_HOP_DOWN", "LEDGE_HOP_UP", "LEDGE_HOP_LEFT", "LEDGE_HOP_RIGHT"
    }

    def is_walkable(x, y):
        if not (0 <= x < width and 0 <= y < height): return False
        cell = grid.get((x,y))
        if not cell: return False

        if cell["type"] in NON_WALKABLE_TYPES:
            return False

        if cell["has_object"]:
            # Exception: Warps might have objects but be walkable? 
            # For now, assume objects block.
            return False

        return True

    queue = deque([ (start, []) ])
    visited = set([start])

    found_buttons = []

    while queue:
        curr, path = queue.popleft()
        cx, cy = curr

        if curr == goal:
            found_buttons = path
            break

        moves = [
            ("Right", 1, 0),
            ("Left", -1, 0),
            ("Down", 0, 1),
            ("Up", 0, -1)
        ]

        for btn, dx, dy in moves:
            nx, ny = cx + dx, cy + dy

            # 1. Normal Move
            if is_walkable(nx, ny):
                if (nx, ny) not in visited:
                    visited.add((nx, ny))
                    queue.append( ((nx, ny), path + [btn]) )

            # 2. Ledge Jump
            if (0 <= nx < width and 0 <= ny < height):
                cell = grid.get((nx, ny))
                if cell:
                    t_type = cell["type"]
                    is_ledge = False

                    if btn == "Right" and t_type == "LEDGE_HOP_RIGHT": is_ledge = True
                    elif btn == "Left" and t_type == "LEDGE_HOP_LEFT": is_ledge = True
                    elif btn == "Down" and t_type == "LEDGE_HOP_DOWN": is_ledge = True
                    elif btn == "Up" and t_type == "LEDGE_HOP_UP": is_ledge = True

                    if is_ledge:
                        lx, ly = cx + (dx*2), cy + (dy*2)
                        if is_walkable(lx, ly):
                            if (lx, ly) not in visited:
                                visited.add((lx, ly))
                                queue.append( ((lx, ly), path + [btn]) )

    print(json.dumps(found_buttons))

except Exception:
    print(json.dumps([]))
