import heapq
import json
import xml.etree.ElementTree as ET

start_x = int(input_data['start_x'])
start_y = int(input_data['start_y'])
end_x = int(input_data['end_x'])
end_y = int(input_data['end_y'])
movement_mode = input_data.get('movement_mode', 'walking')
extra_impassable_str = input_data.get('extra_impassable', '[]')
extra_impassable = json.loads(extra_impassable_str)

print(f"--- Pathfinder Debug Log ---")
print(f"Finding path from ({start_x},{start_y}) to ({end_x},{end_y})")

def heuristic(a, b):
    return abs(a[0] - b[0]) + abs(a[1] - b[1])

def a_star_search(grid, start, goal, impassable_types, extra_impassable_coords):
    neighbors_diff = [(0, 1, 'Down'), (0, -1, 'Up'), (1, 0, 'Right'), (-1, 0, 'Left')]
    close_set = set()
    came_from = {}
    gscore = {start: 0}
    fscore = {start: heuristic(start, goal)}
    oheap = []

    heapq.heappush(oheap, (fscore[start], start))
    goal_reached_node = None

    while oheap:
        current = heapq.heappop(oheap)[1]
        print(f"\nProcessing node: {current}")

        if current == goal:
            print(f"  - Goal {goal} reached!")
            goal_reached_node = current
            break

        close_set.add(current)

        for i, j, direction in neighbors_diff:
            neighbor = current[0] + i, current[1] + j

            if neighbor not in grid:
                print(f"  - Neighbor {neighbor} REJECTED: Out of bounds.")
                continue

            if neighbor in close_set:
                continue

            rejection_reason = None
            if grid[neighbor]['type'] in impassable_types:
                rejection_reason = f"Impassable type '{grid[neighbor]['type']}'"
            elif grid[neighbor]['has_boulder']:
                rejection_reason = "Has boulder"
            elif (neighbor[0], neighbor[1]) in extra_impassable_coords:
                rejection_reason = "In extra impassable list"
            elif grid[neighbor]['has_object']:
                object_name = grid[neighbor].get('object_name')
                ignored_objects = ['Pikachu', 'GUARD', 'COOLTRAINER', 'POKEMANIAC', 'HIKER', 'JUGGLER', 'TAMER']
                is_ignored = any(ignored in object_name for ignored in ignored_objects) if object_name else False
                if object_name and not is_ignored:
                    rejection_reason = f"Blocked by object '{object_name}'"

            if rejection_reason:
                print(f"  - Neighbor {neighbor} REJECTED: {rejection_reason}.")
                continue

            is_valid_move = False
            current_type = grid[current]['type']
            neighbor_type = grid[neighbor]['type']

            if (current_type == 'ladder_up' and direction == 'Up') or (current_type == 'ladder_down' and direction == 'Down'):
                is_valid_move = True
            elif neighbor_type == 'ledge' and direction == 'Down':
                is_valid_move = True
            elif current_type in ['elevated_ground', 'cleared_boulder_barrier'] and neighbor_type == 'ground' and direction == 'Down':
                is_valid_move = True
            else:
                current_is_elevated = current_type in ['elevated_ground', 'cleared_boulder_barrier']
                neighbor_is_elevated = neighbor_type in ['elevated_ground', 'cleared_boulder_barrier']
                if current_is_elevated == neighbor_is_elevated:
                    is_valid_move = True
                elif (current_type == 'steps' and neighbor_type in ['ground', 'elevated_ground', 'cleared_boulder_barrier']) or \
                     (neighbor_type == 'steps' and current_type in ['ground', 'elevated_ground', 'cleared_boulder_barrier']):
                    is_valid_move = True

            if not is_valid_move:
                print(f"  - Neighbor {neighbor} REJECTED: Invalid traversal from '{current_type}' to '{neighbor_type}'.")
                continue

            tentative_g_score = gscore[current] + 1
            gscore_neighbor = gscore.get(neighbor, float('inf'))

            if tentative_g_score < gscore_neighbor:
                print(f"  - Neighbor {neighbor} ACCEPTED. Adding to queue.")
                came_from[neighbor] = current
                gscore[neighbor] = tentative_g_score
                fscore[neighbor] = tentative_g_score + heuristic(neighbor, goal)
                if neighbor not in [item[1] for item in oheap]:
                    heapq.heappush(oheap, (fscore[neighbor], neighbor))
            else:
                print(f"  - Neighbor {neighbor} ignored: Not a better path (gscore {tentative_g_score} >= {gscore_neighbor}).")
    else:
        print("No more nodes in open heap. Path not found.")
        goal_reached_node = None

    if goal_reached_node:
        data = []
        current = goal_reached_node
        while current in came_from:
            data.append(current)
            current = came_from[current]
        data.append(start)
        return [{'x': x, 'y': y} for x, y in reversed(data)]
    else:
        return None

root = ET.fromstring(map_xml_string)
grid = {}
for row_elem in root.findall('Row'):
    y = int(row_elem.get('id'))
    for tile_elem in row_elem.findall('Tile'):
        x = int(tile_elem.get('id'))
        tile_type = tile_elem.get('type')
        obj_node = tile_elem.find('Object')
        has_object = obj_node is not None
        object_name = None
        if has_object:
            object_name = obj_node.get('name') or obj_node.get('id-name')
        has_boulder = tile_elem.find('Boulder') is not None
        grid[(x, y)] = {'type': tile_type, 'has_object': has_object, 'object_name': object_name, 'has_boulder': has_boulder}

start_node = (start_x, start_y)
goal_node = (end_x, end_y)

impassable_types = ['impassable', 'boulder_barrier']
if movement_mode == 'walking':
    impassable_types.append('water')

extra_impassable_coords = {(d['x'], d['y']) for d in extra_impassable}

path = a_star_search(grid, start_node, goal_node, impassable_types, extra_impassable_coords)
if path:
    print(json.dumps(path))
else:
    print(json.dumps({"error": f"No path found to {goal_node}."}))

