import xml.etree.ElementTree as ET
import json

root = ET.fromstring(map_xml_string)
puzzle_data = {
    'player': None,
    'boulders': [],
    'switches': [],
    'barriers': [],
    'map_dimensions': {
        'width': int(root.get('width')),
        'height': int(root.get('height'))
    }
}

for row_elem in root.findall('Row'):
    y = int(row_elem.get('id'))
    for tile_elem in row_elem.findall('Tile'):
        x = int(tile_elem.get('id'))

        if tile_elem.find('Player') is not None:
            puzzle_data['player'] = {'x': x, 'y': y}

        if tile_elem.find('Boulder') is not None:
            puzzle_data['boulders'].append({'x': x, 'y': y})

        if tile_elem.find('BoulderSwitch') is not None:
            is_pressed = tile_elem.find('BoulderSwitch').get('has-boulder') == 'true'
            puzzle_data['switches'].append({'x': x, 'y': y, 'is_pressed': is_pressed})

        if tile_elem.get('type') == 'boulder_barrier':
            puzzle_data['barriers'].append({'x': x, 'y': y})

print(json.dumps(puzzle_data, indent=2))

