import xml.etree.ElementTree as ET
import sys

def find_path_bfs(xml_string, start_pos, target_pos):
    root = ET.fromstring(xml_string)
    width = int(root.attrib['width'])
    height = int(root.attrib['height'])

    grid = {}
    for row in root.findall('Row'):
        y = int(row.attrib['id'])
        for tile in row.findall('Tile'):
            x = int(tile.attrib['id'])
            t_type = tile.attrib['type']
            # Treat unseen as walkable for exploration
            if t_type == 'WALL':
                walkable = False
            elif 'PIT' in t_type or t_type == 'PIT':
                 walkable = False
            else:
                walkable = True
            grid[(x, y)] = walkable

    queue = [[start_pos]]
    visited = {start_pos}

    while queue:
        path = queue.pop(0)
        curr = path[-1]

        if curr == target_pos:
            return path

        x, y = curr
        neighbors = [
            (x+1, y), (x-1, y), (x, y+1), (x, y-1)
        ]

        for nx, ny in neighbors:
            if 0 <= nx < width and 0 <= ny < height:
                if (nx, ny) not in visited and grid.get((nx, ny), False):
                    visited.add((nx, ny))
                    new_path = list(path)
                    new_path.append((nx, ny))
                    queue.append(new_path)

    return None

start = (input_data['start_x'], input_data['start_y'])
