import json

# Robust argument retrieval for drink_name
try:
    drink = drink_name.lower()
except NameError:
    if 'drink_name' in locals():
         drink = locals()['drink_name'].lower()
    elif 'args' in locals():
        drink = args.get('drink_name', '').lower()
    elif 'input_data' in locals():
        drink = input_data.get('drink_name', '').lower()
    else:
        drink = "lemonade" # Default

# Robust argument retrieval for quantity
try:
    qty = int(quantity)
except NameError:
    if 'quantity' in locals():
         qty = int(locals()['quantity'])
    elif 'args' in locals():
        qty = int(args.get('quantity', 1))
    elif 'input_data' in locals():
        qty = int(input_data.get('quantity', 1))
    else:
        qty = 1 # Default

buttons = []
downs = 0
if "soda" in drink:
    downs = 1
elif "lemon" in drink:
    downs = 2

for _ in range(qty):
    # Navigate to drink
    for _ in range(downs):
        buttons.append("Down")
        buttons.append("sleep 300")

    # Select Drink
    buttons.append("A")
    buttons.append("sleep 2000") # Wait for "Clang!" and processing

    # Clear Text (4 screens: Clang -> Popped -> Put -> Pocket -> Menu)
    # Based on observation, 4 Bs brings us back to the menu.
    for _ in range(4):
        buttons.append("B")
        buttons.append("sleep 1000")

    buttons.append("sleep 500") # Extra buffer for menu reload

print(json.dumps(buttons))
