import xml.etree.ElementTree as ET
import json
import collections

# Use global map_xml_string and input_data
root = ET.fromstring(map_xml_string)
autopress = input_data.get('autopress_buttons', False)
current_x = input_data.get('current_x')
current_y = input_data.get('current_y')

grid = {}
for row in root.findall('Row'):
    y = int(row.attrib['id'])
    for tile in row.findall('Tile'):
        x = int(tile.attrib['id'])
        grid[(x, y)] = tile

if current_x is None or current_y is None:
    player_pos = None
    for (x, y), tile in grid.items():
        if tile.find('Player') is not None:
            player_pos = (x, y)
            break
else:
    player_pos = (int(current_x), int(current_y))

if not player_pos:
    if autopress:
        print(json.dumps([]))
    else:
        print(json.dumps({"unseen_tiles": [], "buttons": []}))
else:
    is_surfing = grid[player_pos].attrib['type'] == 'WATER'

    def can_move(from_pos, to_pos, direction):
        if to_pos not in grid: return False
        f_tile = grid[from_pos]
        t_tile = grid[to_pos]
        f_type = f_tile.attrib['type']
        t_type = t_tile.attrib['type']

        if t_tile.attrib['seen'] == 'false': return False
        if t_type == 'WALL': return False
        if t_tile.find('Object') is not None: return False
        if t_type == 'WATER' and not is_surfing: return False
        if t_type == 'WATERFALL' and not is_surfing: return False

        # Directional collisions
        if direction == 'Down':
            if f_type == 'WALL_DOWN_FLOOR': return False
            if t_type == 'FLOOR_UP_WALL': return False
        elif direction == 'Up':
            if f_type == 'WALL_UP_FLOOR': return False
            if t_type == 'FLOOR_DOWN_WALL': return False
        elif direction == 'Right':
            if f_type == 'WALL_RIGHT_FLOOR': return False
            if t_type == 'FLOOR_LEFT_WALL': return False
        elif direction == 'Left':
            if f_type == 'WALL_LEFT_FLOOR': return False
            if t_type == 'FLOOR_RIGHT_WALL': return False

        # Ledges
        if t_type == 'LEDGE_DOWN': return direction == 'Down'
        if t_type == 'LEDGE_LEFT': return direction == 'Left'
        if t_type == 'LEDGE_RIGHT': return direction == 'Right'

        return True

    queue = collections.deque([player_pos])
    visited = {player_pos}
    reachable_walkable = set()
    while queue:
        cx, cy = queue.popleft()
        reachable_walkable.add((cx, cy))
        for dx, dy, btn in [(0, 1, 'Down'), (0, -1, 'Up'), (1, 0, 'Right'), (-1, 0, 'Left')]:
            nx, ny = cx + dx, cy + dy
            if (nx, ny) in grid:
                if can_move((cx, cy), (nx, ny), btn) and (nx, ny) not in visited:
                    visited.add((nx, ny))
                    queue.append((nx, ny))

    reachable_unseen = []
    for (cx, cy) in reachable_walkable:
        for dx, dy in [(0, 1), (0, -1), (1, 0), (-1, 0)]:
            nx, ny = cx + dx, cy + dy
            if (nx, ny) in grid and grid[(nx, ny)].attrib['seen'] == 'false':
                if [nx, ny] not in reachable_unseen:
                    reachable_unseen.append([nx, ny])

    path_found = []
    if reachable_unseen:
        queue = collections.deque([(player_pos, [])])
        visited_path = {player_pos}
        while queue:
            (cx, cy), path = queue.popleft()
            for dx, dy, btn in [(0, 1, 'Down'), (0, -1, 'Up'), (1, 0, 'Right'), (-1, 0, 'Left')]:
                nx, ny = cx + dx, cy + dy
                if (nx, ny) in grid:
                    if grid[(nx, ny)].attrib['seen'] == 'false':
                        # Check if we can stand next to it
                        path_found = path + [btn]
                        break
                    if can_move((cx, cy), (nx, ny), btn) and (nx, ny) not in visited_path:
                        visited_path.add((nx, ny))
                        queue.append(((nx, ny), path + [btn]))
                if path_found: break
            if path_found: break

    if autopress:
        print(json.dumps(path_found))
    else:
        print(json.dumps({"unseen_tiles": reachable_unseen, "buttons": path_found}))

find_path_v7_robust.python_script:
import xml.etree.ElementTree as ET
import json
import collections

root = ET.fromstring(map_xml_string)
autopress = input_data.get('autopress_buttons', False)
start_x = int(input_data['start_x'])
start_y = int(input_data['start_y'])
end_x = int(input_data['end_x'])
end_y = int(input_data['end_y'])
initial_surfing = input_data.get('is_surfing', False)

grid = {}
for row in root.findall('Row'):
    y = int(row.attrib['id'])
    for tile in row.findall('Tile'):
        x = int(tile.attrib['id'])
        grid[(x, y)] = tile

def can_move(from_pos, to_pos, direction, currently_surfing):
    if to_pos not in grid: return False
    f_tile = grid[from_pos]
    t_tile = grid[to_pos]
    f_type = f_tile.attrib['type']
    t_type = t_tile.attrib['type']

    if t_tile.attrib['seen'] == 'false': return True
    if t_type == 'WALL': return False
    if t_tile.find('Object') is not None: return False

    if t_type in ['WATER', 'WATERFALL']:
        if not currently_surfing: return False

    # Generic directional blocking logic (Verified N/S blocking for UP_WALL/UP_FLOOR)
    if 'UP_WALL' in t_type or 'UP_FLOOR' in t_type:
        if direction in ['Up', 'Down']: return False
    if 'DOWN_WALL' in t_type or 'DOWN_FLOOR' in t_type:
        if direction in ['Up', 'Down']: return False
    if 'LEFT_WALL' in t_type or 'LEFT_FLOOR' in t_type:
        if direction in ['Left', 'Right']: return False
    if 'RIGHT_WALL' in t_type or 'RIGHT_FLOOR' in t_type:
        if direction in ['Left', 'Right']: return False

    # From tile exit blocks
    if 'UP_WALL' in f_type or 'UP_FLOOR' in f_type:
        if direction == 'Up': return False
    if 'DOWN_WALL' in f_type or 'DOWN_FLOOR' in f_type:
        if direction == 'Down': return False
    if 'LEFT_WALL' in f_type or 'LEFT_FLOOR' in f_type:
        if direction == 'Left': return False
    if 'RIGHT_WALL' in f_type or 'RIGHT_FLOOR' in f_type:
        if direction == 'Right': return False

    if t_type == 'LEDGE_DOWN' and direction != 'Down': return False
    if t_type == 'LEDGE_LEFT' and direction != 'Left': return False
    if t_type == 'LEDGE_RIGHT' and direction != 'Right': return False

    return True

queue = collections.deque([((start_x, start_y), initial_surfing, [])])
visited = {(start_x, start_y, initial_surfing)}
found_path = None

while queue:
    (cx, cy), surfing, path = queue.popleft()
    if (cx, cy) == (end_x, end_y):
        found_path = path
        break

    for dx, dy, btn in [(0, 1, 'Down'), (0, -1, 'Up'), (1, 0, 'Right'), (-1, 0, 'Left')]:
        nx, ny = cx + dx, cy + dy
        if (nx, ny) in grid:
            ntile = grid[(nx, ny)]
            ntype = ntile.attrib['type']
            new_surfing = surfing
            if ntype in ['WATER', 'WATERFALL']:
                new_surfing = True
            elif ntype in ['FLOOR', 'CAVE', 'WARP_CARPET_DOWN']:
                new_surfing = False

            if (nx, ny, new_surfing) not in visited:
                if can_move((cx, cy), (nx, ny), btn, surfing):
                    visited.add((nx, ny, new_surfing))
                    queue.append(((nx, ny), new_surfing, path + [btn]))

if found_path is not None:
    if autopress: print(json.dumps(found_path[:50]))
    else: print(json.dumps({"path": found_path}))
else:
    if autopress: print(json.dumps([]))
    else: print(json.dumps({"path": []}))
