import heapq
import xml.etree.ElementTree as ET
import json

def find_path(input_data):
    map_xml_string = globals().get('map_xml_string', '')
    if not map_xml_string:
        print(json.dumps({'path': [], 'message': 'Error: map_xml_string not found in global scope.'}))
        return

    try:
        start_x = int(input_data['start_x'])
        start_y = int(input_data['start_y'])
        target_x = int(input_data['target_x'])
        target_y = int(input_data['target_y'])
    except (ValueError, KeyError) as e:
        print(json.dumps({'path': [], 'message': f'Error: Invalid input format. {e}'}))
        return

    try:
        root = ET.fromstring(map_xml_string)
        grid = {}
        for row in root.findall('Row'):
            for tile in row.findall('Tile'):
                coord_str = tile.get('coordinate')
                if not coord_str: continue
                coords = coord_str.strip('()').split(',')
                x = int(coords[0].strip())
                y = int(coords[1].strip())
                grid[(x, y)] = tile
    except (ET.ParseError, ValueError) as e:
        print(json.dumps({'path': [], 'message': f'Error: Failed to parse map XML. {e}'}))
        return

    def is_walkable(x, y, grid_map):
        tile = grid_map.get((x, y))
        if not tile: return False
        tile_type = tile.get('type')
        if tile_type in ['ground', 'ledge']:
             return True
        return False

    def get_neighbors(x, y, grid_map):
        neighbors = []
        current_tile = grid_map.get((x,y))
        if not current_tile: return []
        current_tile_type = current_tile.get('type')
        if current_tile_type == 'ledge':
            if is_walkable(x, y + 1, grid_map):
                neighbors.append(((x, y + 2), 1))
            return neighbors
        for dx, dy in [(0, 1), (0, -1), (1, 0), (-1, 0)]:
            nx, ny = x + dx, y + dy
            neighbor_tile = grid_map.get((nx, ny))
            if not neighbor_tile: continue
            neighbor_tile_type = neighbor_tile.get('type')
            if neighbor_tile_type == 'ledge' and dy == -1: continue
            if is_walkable(nx, ny, grid_map):
                neighbors.append(((nx, ny), 1))
        return neighbors

    start_node = (start_x, start_y)
    target_node = (target_x, target_y)

    if not is_walkable(target_x, target_y, grid):
        temp_neighbors = []
        for dx_n, dy_n in [(0, 1), (0, -1), (1, 0), (-1, 0)]:
            nx_n, ny_n = target_x + dx_n, target_y + dy_n
            if is_walkable(nx_n, ny_n, grid):
                temp_neighbors.append((nx_n, ny_n))
        if temp_neighbors:
            target_node = min(temp_neighbors, key=lambda p: (p[0]-start_x)**2 + (p[1]-start_y)**2)
        else:
            print(json.dumps({'path': [], 'message': f'Target ({target_x},{target_y}) and all its adjacent tiles are unwalkable.'}))
            return

    open_set = [(0, start_node)]
    came_from = {}
    g_score = {node: float('inf') for node in grid}
    g_score[start_node] = 0
    f_score[start_node] = abs(start_x - target_node[0]) + abs(start_y - target_node[1])

    while open_set:
        _, current = heapq.heappop(open_set)
        if current == target_node:
            path = []
            while current in came_from:
                path.append(current)
                current = came_from[current]
            path.append(start_node)
            print(json.dumps({'path': path[::-1]}))
            return
        for neighbor, cost in get_neighbors(current[0], current[1], grid):
            tentative_g_score = g_score[current] + cost
            if tentative_g_score < g_score.get(neighbor, float('inf')):
                came_from[neighbor] = current
                g_score[neighbor] = tentative_g_score
                f_score[neighbor] = tentative_g_score + abs(neighbor[0] - target_node[0]) + abs(neighbor[1] - target_node[1])
                if neighbor not in [i[1] for i in open_set]:
                    heapq.heappush(open_set, (f_score[neighbor], neighbor))

    print(json.dumps({'path': [], 'message': f'No path found from ({start_x},{start_y}) to ({target_x},{target_y}). The area may be blocked.'}))

find_path(input_data)
