import xml.etree.ElementTree as ET
from collections import deque
import json

def get_map_data(map_xml_string):
    root = ET.fromstring(map_xml_string)
    map_width = int(root.get('width'))
    map_height = int(root.get('height'))
    grid = {}
    objects = set()
    start_x, start_y = None, None
    for row_elem in root.findall('Row'):
        y = int(row_elem.get('id'))
        for tile_elem in row_elem.findall('Tile'):
            x = int(tile_elem.get('id'))
            grid[(x, y)] = tile_elem.get('type')
            if tile_elem.find('Player') is not None:
                start_x, start_y = x, y
            if tile_elem.find('Object') is not None:
                obj = tile_elem.find('Object')
                if obj.get('id-name') != 'PIKACHU':
                    objects.add((x, y))
    return map_width, map_height, grid, objects, start_x, start_y

def has_conscious_hm_user(party, hm_name):
    for pokemon in party:
        if not pokemon.get('is_fainted', False):
            for move in pokemon.get('moves', []):
                if move == hm_name:
                    return True
    return False

def find_path(map_xml_string, target_x, target_y, party_data_str):
    try:
        party = json.loads(party_data_str)
    except json.JSONDecodeError:
        return None, 'Invalid party_data JSON.'

    map_width, map_height, grid, objects, start_x, start_y = get_map_data(map_xml_string)
    if start_x is None:
        return None, 'Player start position not found.'

    start_pos = (start_x, start_y)
    walkable_land = ['ground', 'grass', 'steps']

    q = deque([(start_pos, [start_pos])])
    visited = {start_pos}

    target_pos = (target_x, target_y)
    target_tile_type = grid.get(target_pos, 'impassable')
    target_is_impassable = target_tile_type not in walkable_land and target_tile_type != 'water' or target_pos in objects

    def is_valid_for_pathing(current_pos, next_pos):
        current_type = grid.get(current_pos)
        next_type = grid.get(next_pos)
        if next_pos in objects or next_type is None or next_type == 'impassable':
            return False
        if current_type in walkable_land:
            if next_type in walkable_land or next_type == 'ledge':
                return True
            if next_type == 'water':
                return has_conscious_hm_user(party, 'SURF')
        elif current_type == 'water':
            if next_type == 'water' or next_type in walkable_land:
                return True
        return False

    while q:
        current_pos, path = q.popleft()
        current_x, current_y = current_pos

        if not target_is_impassable and current_pos == target_pos:
            return path, None
        elif target_is_impassable and abs(current_x - target_x) + abs(current_y - target_y) == 1:
             if grid.get(current_pos) in walkable_land or grid.get(current_pos) == 'water':
                 return path, None

        moves = [(0, -1), (0, 1), (-1, 0), (1, 0)]

        for dx, dy in moves:
            next_pos = (current_x + dx, current_y + dy)
            if not (1 <= next_pos[0] <= map_width and 1 <= next_pos[1] <= map_height) or next_pos in visited:
                continue

            if is_valid_for_pathing(current_pos, next_pos):
                next_path = path + [next_pos]
                if grid.get(next_pos) == 'ledge':
                    land_pos = (next_pos[0], next_pos[1] + 1)
                    if 1 <= land_pos[1] <= map_height and land_pos not in visited:
                        visited.add(next_pos)
                        visited.add(land_pos)
                        q.append((land_pos, next_path + [land_pos]))
                else:
                    visited.add(next_pos)
                    q.append((next_pos, next_path))
    return None, 'No path found.'

try:
    target_x = int(input_data['target_x'])
    target_y = int(input_data['target_y'])
    party_data_str = input_data['party_data']
    path_result, error_msg = find_path(map_xml_string, target_x, target_y, party_data_str)
    if path_result is not None:
        path_plan = [{'x': x, 'y': y} for x, y in path_result]
        print(json.dumps({'path_found': True, 'path_plan': path_plan}))
    else:
        print(json.dumps({'path_found': False, 'error': error_msg}))
except Exception as e:
    print(json.dumps({'path_found': False, 'error': f"An unexpected error occurred: {str(e)}"}))

spinner_maze_solver.python_script:
import xml.etree.ElementTree as ET
import heapq

def spinner_maze_solver(input_data, map_xml_string):
    try:
        target_x = int(input_data['target_x'])
        target_y = int(input_data['target_y'])
        root = ET.fromstring(map_xml_string)
    except (ValueError, ET.ParseError):
        return 'Error: Invalid input or map data.'

    tile_map = {}
    player_pos = None
    width = int(root.get('width'))
    height = int(root.get('height'))

    for row_elem in root.findall('Row'):
        y = int(row_elem.get('id'))
        for tile_elem in row_elem.findall('Tile'):
            x = int(tile_elem.get('id'))
            tile_type = tile_elem.get('type')
            has_obj = tile_elem.find('Object') is not None
            obj_is_pikachu = False
            if has_obj:
                obj_elem = tile_elem.find('Object')
                if obj_elem.get('id-name') == 'Pikachu':
                    obj_is_pikachu = True

            spinner_info = tile_elem.find('Spinner')
            spinner_direction = spinner_info.get('direction') if spinner_info is not None else None
            tile_map[(x, y)] = {'type': tile_type, 'has_obj': has_obj, 'obj_is_pikachu': obj_is_pikachu, 'spinner': spinner_direction}

            if tile_elem.find('Player') is not None:
                player_pos = (x, y)

    if not player_pos:
        return 'Error: Player position not found.'

    def is_walkable(coord, ignore_spinner=False):
        if coord not in tile_map: return False
        tile = tile_map[coord]
        if tile['has_obj'] and not tile['obj_is_pikachu']: return False
        walkable_types = ['ground', 'grass', 'steps', 'cleared_boulder_barrier', 'open_gate', 'gate_offscreen', 'teleport', 'ladder_up', 'ladder_down', 'spinner_stop']
        if not ignore_spinner and tile['spinner']:
             walkable_types.extend(['spinner_up', 'spinner_down', 'spinner_left', 'spinner_right'])
        return tile['type'] in walkable_types

    def get_spinner_destination(start_coord):
        x, y = start_coord
        path = [start_coord]
        visited_spinners = {start_coord}
        while True:
            if (x, y) not in tile_map or not tile_map[(x, y)]['spinner']:
                return (x, y), path

            direction = tile_map[(x, y)]['spinner']
            if direction == 'up': y -= 1
            elif direction == 'down': y += 1
            elif direction == 'left': x -= 1
            elif direction == 'right': x += 1

            if (x, y) in visited_spinners: return None, None # Infinite loop
            visited_spinners.add((x, y))
            path.append((x,y))

            if not (1 <= x <= width and 1 <= y <= height):
                 return None, None # Out of bounds

    target_node = (target_x, target_y)
    is_target_impassable = not is_walkable(target_node, ignore_spinner=True)

    def heuristic(a, b):
        return abs(a[0] - b[0]) + abs(a[1] - b[1])

    open_set = [(0, player_pos)]
    came_from = {}
    g_score = {player_pos: 0}

    while open_set:
        _, current = heapq.heappop(open_set)

        if not is_target_impassable and current == target_node:
            break
        if is_target_impassable and heuristic(current, target_node) == 1 and is_walkable(current):
            break

        # Standard moves
        for dx, dy in [(0, 1), (0, -1), (1, 0), (-1, 0)]:
            neighbor = (current[0] + dx, current[1] + dy)
            if not is_walkable(neighbor): continue

            tentative_g_score = g_score[current] + 1
            if neighbor not in g_score or tentative_g_score < g_score[neighbor]:
                g_score[neighbor] = tentative_g_score
                f_score = tentative_g_score + heuristic(neighbor, target_node)
                heapq.heappush(open_set, (f_score, neighbor))
                came_from[neighbor] = current

        # Spinner move
        if tile_map[current]['spinner']:
            dest, spin_path = get_spinner_destination(current)
            if dest and is_walkable(dest):
                tentative_g_score = g_score[current] + len(spin_path) # Cost is path length
                if dest not in g_score or tentative_g_score < g_score[dest]:
                    g_score[dest] = tentative_g_score
                    f_score = tentative_g_score + heuristic(dest, target_node)
                    heapq.heappush(open_set, (f_score, dest))
                    came_from[dest] = current # The start of the spin sequence

    else:
        return 'No path found.'

    # Reconstruct path
    path = []
    temp_current = current
    while temp_current in came_from:
        path.append(temp_current)
        temp_current = came_from[temp_current]
    path.append(player_pos)
    path.reverse()

    # Expand spinner sequences in the final path
    final_path = []
    for i in range(len(path) - 1):
        final_path.append(path[i])
        if tile_map[path[i]]['spinner'] and came_from.get(path[i+1]) == path[i]:
            _, spin_path = get_spinner_destination(path[i])
            # Exclude start and end as they are already in the main path
            if spin_path and len(spin_path) > 2:
                final_path.extend(spin_path[1:-1])
    final_path.append(path[-1])

    return final_path

print(spinner_maze_solver(input_data, map_xml_string))

menu_list_navigator.python_script:
import json

try:
    menu_items = json.loads(input_data['menu_items'])
    current_selection = input_data['current_selection']
    target_selection = input_data['target_selection']

    if current_selection not in menu_items or target_selection not in menu_items:
        print(json.dumps({'success': False, 'error': 'Current or target selection not found in menu items.'}))
    else:
        current_idx = menu_items.index(current_selection)
        target_idx = menu_items.index(target_selection)

        delta = target_idx - current_idx
        button_presses = []
        if delta > 0:
            button_presses = ['Down'] * delta
        elif delta < 0:
            button_presses = ['Up'] * abs(delta)

        print(json.dumps({'success': True, 'button_presses': button_presses}))
except Exception as e:
    print(json.dumps({'success': False, 'error': f'An unexpected error occurred: {str(e)}'}))
