find_path_v4.python_script:
import xml.etree.ElementTree as ET
from collections import deque
import json

def solve():
    try:
        target_x = int(input_data['target_x'])
        target_y = int(input_data['target_y'])

        root = ET.fromstring(map_xml_string)

        start_x, start_y = -1, -1
        for row in root.findall('Row'):
            curr_y = int(row.attrib['id'])
            for tile in row.findall('Tile'):
                if tile.find('Player') is not None:
                    start_x = int(tile.attrib['id'])
                    start_y = curr_y
                    break
            if start_x != -1: break

        if start_x == -1: return []

        grid = {}
        for row in root.findall('Row'):
            y = int(row.attrib['id'])
            for tile in row.findall('Tile'):
                x = int(tile.attrib['id'])
                t_type = tile.attrib['type']
                is_wall = t_type in ['WALL', 'COUNTER', 'BOOKSHELF', 'TABLE', 'MART_SHELF']
                obj = tile.find('Object')
                if obj is not None:
                    # NPCs and items are walls unless they are the target (for interaction)
                    if not (x == start_x and y == start_y) and not (x == target_x and y == target_y):
                        is_wall = True
                grid[(x, y)] = not is_wall

        queue = deque([(start_x, start_y, [])])
        visited = {(start_x, start_y)}
        while queue:
            x, y, path = queue.popleft()
            if x == target_x and y == target_y:
                return path
            for dx, dy, btn in [(0, -1, 'Up'), (0, 1, 'Down'), (-1, 0, 'Left'), (1, 0, 'Right')]:
                nx, ny = x + dx, y + dy
                if (nx, ny) in grid and grid[(nx, ny)] and (nx, ny) not in visited:
                    visited.add((nx, ny))
                    queue.append((nx, ny, path + [btn]))
        return []
    except Exception:
        return []

print(json.dumps(solve()))

check_reachable_unseen_v1.python_script:
import json
import xml.etree.ElementTree as ET

def main():
    # map_xml_string is automatically provided by the harness
    root = ET.fromstring(map_xml_string)

    # Based on the notepad, these are traversable
    walkable_types = ["FLOOR", "STAIRCASE", "WARP_CARPET", "LADDER"]
    reachable_unseen = []

    # Create a lookup for tiles
    tiles = {}
    for row in root.findall('Row'):
        y = int(row.get('id'))
        for tile in row.findall('Tile'):
            x = int(tile.get('id'))
            tiles[(x, y)] = tile

    # Find unseen tiles adjacent to seen walkable tiles
    for (x, y), tile in tiles.items():
        if tile.get('seen') == 'false':
            for dx, dy in [(0, 1), (0, -1), (1, 0), (-1, 0)]:
                neighbor = tiles.get((x + dx, y + dy))
                if neighbor is not None and neighbor.get('seen') == 'true' and neighbor.get('type') in walkable_types:
                    reachable_unseen.append({"x": x, "y": y})
                    break

    print(json.dumps(reachable_unseen))

if __name__ == "__main__":
    main()

find_object_by_name_v1.python_script:
import json
import xml.etree.ElementTree as ET

def main(input_data):
    # map_xml_string is automatically provided by the harness
    root = ET.fromstring(map_xml_string)
    target_name = input_data['object_name'].upper()
    results = []

    for row in root.findall('Row'):
        y = int(row.get('id'))
        for tile in row.findall('Tile'):
            x = int(tile.get('id'))

            # Check Map Objects (NPCs, items)
            for obj in tile.findall('Object'):
                obj_name = obj.get('id-name', '').upper()
                if target_name in obj_name:
                    results.append({"name": obj.get('id-name'), "x": x, "y": y, "type": "MapObject"})

            # Check Background Objects (Signs, PCs, Vending Machines)
            bg_obj_name = tile.get('name', '').upper()
            if target_name in bg_obj_name:
                results.append({"name": tile.get('name'), "x": x, "y": y, "type": "BackgroundObject"})

    print(json.dumps(results))

if __name__ == "__main__":
    main(input_data)

shutter_status_report_v2.python_script:
import xml.etree.ElementTree as ET

def main():
    try:
        root = ET.fromstring(map_xml_string)
        shutter_coords = [
            (2, 6), (3, 6), (10, 6), (16, 6), (6, 8), (12, 8), (17, 6), (17, 7)
        ]

        px, py = -1, -1
        for row in root.findall('Row'):
            y = int(row.get('id'))
            for tile in row.findall('Tile'):
                if tile.find('Player') is not None:
                    px = int(tile.get('id'))
                    py = y
                    break
            if px != -1: break

        report = ["--- Shutter Status Report v2 ---"]
        report.append(f"Player Position: ({px}, {py})")
        report.append("WARNING: Status is based on last visual confirmation. Tiles update only when seen.")

        for x, y in shutter_coords:
            tile = root.find(f"./Row[@id='{y}']/Tile[@id='{x}']")
            if tile is not None:
                tile_type = tile.get('type')
                seen = tile.get('seen')
                # On-screen check: 11x9 window centered on player
                on_screen = "YES" if (px != -1 and abs(x - px) <= 5 and abs(y - py) <= 4) else "NO"

                status = "OPEN" if tile_type == "FLOOR" else "CLOSED"
                if seen == "false":
                    status = "UNSEEN"

                report.append(f"({x}, {y}): {status} (On Screen: {on_screen})")
            else:
                report.append(f"({x}, {y}): NOT IN MAP DATA")

        print("\n".join(report))
    except Exception as e:
        print(f"Error: {e}")

if __name__ == "__main__":
    main()
