import xml.etree.ElementTree as ET
from collections import deque

def main(input_data):
    start_x = input_data['start_x']
    start_y = input_data['start_y']
    target_x = input_data.get('target_x')
    target_y = input_data.get('target_y')
    target_min_x = input_data.get('target_min_x')

    try:
        root = ET.fromstring(map_xml_string)
        width = int(root.attrib['width'])
        height = int(root.attrib['height'])

        grid = {}
        # Known obstacles
        obstacles = {
            'TYPE_2889', # Tree Wall
            'TYPE_5519', # Cut Tree
            'TYPE_c453', # Ledge (Treat as obstacle for safety)
        }

        for row in root.findall('Row'):
            y = int(row.attrib['id'])
            for tile in row.findall('Tile'):
                x = int(tile.attrib['id'])
                t_type = tile.attrib.get('type', 'unseen')

                is_walkable = True
                if t_type in obstacles:
                    is_walkable = False

                # Check markers for manual overrides
                markers = tile.findall('Marker')
                for m in markers:
                    if 'Blocked' in m.text or 'Wall' in m.text or 'Dead End' in m.text:
                        is_walkable = False

                grid[(x, y)] = is_walkable

        # BFS
        queue = deque([(start_x, start_y, [])])
        visited = set([(start_x, start_y)])

        found_path = None

        while queue:
            cx, cy, path = queue.popleft()

            if target_x is not None and target_y is not None:
                if cx == target_x and cy == target_y:
                    found_path = path + [(cx, cy)]
                    break
            elif target_min_x is not None:
                if cx >= target_min_x:
                    found_path = path + [(cx, cy)]
                    break

            for dx, dy in [(0, 1), (0, -1), (1, 0), (-1, 0)]:
                nx, ny = cx + dx, cy + dy
                if 0 <= nx < width and 0 <= ny < height:
                    if (nx, ny) not in visited and grid.get((nx, ny), False):
                        visited.add((nx, ny))
                        queue.append((nx, ny, path + [(cx, cy)]))

        if found_path:
            print(f"Path found (Length {len(found_path)})")
        else:
            print("No path found.")

    except Exception as e:
        print(f"Error: {e}")

if __name__ == "__main__":
    main(input_data)

