import xml.etree.ElementTree as ET
from collections import deque

def bfs_path(xml_string, start, target):
    try:
        root = ET.fromstring(xml_string)
        rows = {int(r.get('id')): r for r in root.findall('Row')}

        # Parse map to find walkable tiles
        walkable = set()
        for y, row in rows.items():
            for tile in row.findall('Tile'):
                # Heuristic: TYPE_3fe2 (Floor), TYPE_80fc (Grass) are usually walkable.
                # Adjust this list based on game knowledge.
                t_type = tile.get('type')
                if t_type in ["TYPE_3fe2", "TYPE_80fc", "TYPE_a336", "TYPE_a339"]: 
                    walkable.add((int(tile.get('id')), y))

        start_tuple = (start['x'], start['y'])
        target_tuple = (target['x'], target['y'])

        if start_tuple not in walkable:
            print(f"Start {start_tuple} is not a known walkable tile.")
            # Depending on strictness, might return False or try anyway

        if target_tuple not in walkable:
            print(f"Target {target_tuple} is not a known walkable tile.")
            return False, 0

        queue = deque([(start_tuple, 0)])
        visited = {start_tuple}

        while queue:
            curr, dist = queue.popleft()
            if curr == target_tuple:
                return True, dist

            x, y = curr
            neighbors = [(x+1, y), (x-1, y), (x, y+1), (x, y-1)]
            for nx, ny in neighbors:
                if (nx, ny) in walkable and (nx, ny) not in visited:
                    visited.add((nx, ny))
                    queue.append(((nx, ny), dist + 1))

        return False, 0

    except Exception as e:
        print(f"Error parsing map or pathfinding: {e}")
        return False, 0

# Expects map_xml_string to be available in the global scope (injected by system)
# Tool arguments provided in input_data variable
start = {'x': input_data['start_x'], 'y': input_data['start_y']}
target = {'x': input_data['target_x'], 'y': input_data['target_y']}

exists, length = bfs_path(map_xml_string, start, target)
print(f"Path exists: {exists}, Length: {length}")
