import xml.etree.ElementTree as ET
import heapq
import json

class AStar:
    def __init__(self, map_xml_string):
        self.root = ET.fromstring(map_xml_string)
        self.width = int(self.root.get('width'))
        self.height = int(self.root.get('height'))
        self.player_pos = self._get_player_pos()
        self.barriers = self._get_barrier_locations()
        self.switches = self._get_switch_locations()

    def _get_player_pos(self):
        for row in self.root.findall('Row'):
            for tile in row.findall('Tile'):
                if tile.find('Player') is not None:
                    return int(tile.get('id')), int(row.get('id'))
        return None

    def _get_barrier_locations(self):
        barriers = set()
        for row in self.root.findall('Row'):
            for tile in row.findall('Tile'):
                if tile.get('type') == 'boulder_barrier':
                    barriers.add((int(tile.get('id')), int(row.get('id'))))
        return barriers

    def _get_switch_locations(self):
        switches = set()
        for row in self.root.findall('Row'):
            for tile in row.findall('Tile'):
                if tile.get('type') == 'boulder_switch':
                    switches.add((int(tile.get('id')), int(row.get('id'))))
        return switches

    def find_path(self, target_x, target_y, ignorable_coords_str=None, is_surfing=False):
        if not self.player_pos:
            print(json.dumps({"error": "Player position not found."}))
            return

        pre_activated_switches = set()
        for row in self.root.findall('Row'):
            for tile in row.findall('Tile'):
                if tile.find('BoulderSwitch') is not None and tile.find('BoulderSwitch').get('has-boulder') == 'true':
                    pre_activated_switches.add((int(tile.get('id')), int(row.get('id'))))

        start_node = (self.player_pos, frozenset(pre_activated_switches))
        end_node_coord = (int(target_x), int(target_y))
        ignorable_coords = []
        if ignorable_coords_str:
            try:
                ignorable_coords = json.loads(ignorable_coords_str)
            except (json.JSONDecodeError, TypeError):
                pass

        traversable_tiles = {'ground', 'grass', 'elevated_ground', 'steps', 'cleared_boulder_barrier', 'boulder_switch', 'ladder_up', 'ladder_down', 'boulder_barrier'}
        if is_surfing:
            traversable_tiles.add('water')

        initial_impassable = self._get_impassable_objects(ignorable_coords)

        open_list = []
        counter = 0
        heapq.heappush(open_list, (0, counter, start_node))
        closed_set = set()

        came_from = {}
        g_score = {start_node: 0}

        visited_count = 0
        while open_list:
            visited_count += 1
            if visited_count > 4000: # Safety break
                print(json.dumps({"error": "Pathfinding timed out. Too many nodes visited."}))
                return

            _, _, current_node = heapq.heappop(open_list)

            if current_node in closed_set:
                continue
            closed_set.add(current_node)

            current_coord, activated_switches = current_node

            if current_coord == end_node_coord:
                path = self.reconstruct_path(came_from, current_node)
                print(path)
                return

            current_impassable = set(initial_impassable)
            if activated_switches:
                current_impassable.difference_update(self.barriers)

            for neighbor_coord in self.get_neighbors(current_coord):
                neighbor_tile_element = self.root.find(f".//Row[@id='{neighbor_coord[1]}']/Tile[@id='{neighbor_coord[0]}']")
                if neighbor_tile_element is None:
                    continue

                newly_activated_switches = set(activated_switches)
                if neighbor_tile_element.get('type') == 'boulder_switch':
                    newly_activated_switches.add(neighbor_coord)

                neighbor_node = (neighbor_coord, frozenset(newly_activated_switches))

                if not self.is_traversable(current_coord, neighbor_coord, traversable_tiles, current_impassable):
                    continue

                tentative_g_score = g_score.get(current_node, float('inf')) + 1

                if tentative_g_score < g_score.get(neighbor_node, float('inf')):
                    came_from[neighbor_node] = current_node
                    g_score[neighbor_node] = tentative_g_score
                    f_score = tentative_g_score + self.heuristic(neighbor_coord, end_node_coord)
                    counter += 1
                    heapq.heappush(open_list, (f_score, counter, neighbor_node))

        print(json.dumps({"error": "No path found."}))

    def heuristic(self, a, b):
        return abs(a[0] - b[0]) + abs(a[1] - b[1])

    def get_neighbors(self, pos):
        x, y = pos
        neighbors = []
        if x > 1: neighbors.append((x - 1, y))
        if x < self.width: neighbors.append((x + 1, y))
        if y > 1: neighbors.append((x, y - 1))
        if y < self.height: neighbors.append((x, y + 1))
        return neighbors

    def is_traversable(self, current_pos, neighbor_pos, traversable_types, impassable_objects):
        n_x, n_y = neighbor_pos
        c_x, c_y = current_pos
        print(f"DEBUG: Checking neighbor ({n_x},{n_y}) from ({c_x},{c_y})")

        tile_element = self.root.find(f".//Row[@id='{n_y}']/Tile[@id='{n_x}']")
        if tile_element is None:
            print(f"  -> FAIL: Tile element not found.")
            return False

        if (n_x, n_y) in impassable_objects:
            print(f"  -> FAIL: In impassable_objects set.")
            return False

        neighbor_type = tile_element.get('type')
        current_tile_element = self.root.find(f".//Row[@id='{c_y}']/Tile[@id='{c_x}']")
        current_type = current_tile_element.get('type')
        print(f"  -> Current type: {current_type}, Neighbor type: {neighbor_type}")

        if neighbor_type not in traversable_types and neighbor_type != 'ledge':
            print(f"  -> FAIL: Neighbor type '{neighbor_type}' not in traversable_types.")
            return False

        if current_type == 'cleared_boulder_barrier' and neighbor_type == 'ground':
            print(f"  -> FAIL: Cannot move from cleared_boulder_barrier to ground.")
            return False

        if neighbor_type == 'ledge':
            if n_y > c_y: print(f"  -> PASS: Ledge jump."); return True
            else: print(f"  -> FAIL: Invalid ledge move."); return False

        if 'ladder' in current_type and neighbor_type in {'ground', 'elevated_ground'}:
            print(f"  -> PASS: Ladder to ground/elevated_ground.")
            return True
        if 'ladder' in neighbor_type and current_type in {'ground', 'elevated_ground'}:
            print(f"  -> PASS: Ground/elevated_ground to ladder.")
            return True

        elevation_map = {
            'ground': 0, 'grass': 0, 'boulder_switch': 0, 'hole': 0, 'water': 0,
            'steps': 1, 'cleared_boulder_barrier': 1, 'boulder_barrier': 1,
            'ladder_up': 1, 'ladder_down': 1,
            'elevated_ground': 2
        }

        current_elevation = elevation_map.get(current_type, -1)
        neighbor_elevation = elevation_map.get(neighbor_type, -1)

        if current_elevation != -1 and neighbor_elevation != -1:
            if abs(current_elevation - neighbor_elevation) > 1:
                print(f"  -> FAIL: Elevation difference too large ({current_elevation} -> {neighbor_elevation}).")
                return False

        print(f"  -> PASS: All checks passed.")
        return True

    def _get_impassable_objects(self, ignorable_coords):
        objects = set()
        map_id = self.root.get('id')
        for row in self.root.findall('Row'):
            for tile in row.findall('Tile'):
                x, y = int(tile.get('id')), int(row.get('id'))
                is_ignorable = False
                if ignorable_coords:
                    is_ignorable = any(d['x'] == x and d['y'] == y for d in ignorable_coords if isinstance(d, dict))
                if is_ignorable:
                    continue

                tile_type = tile.get('type')
                if tile_type in {'impassable', 'boulder_barrier'}:
                    objects.add((x, y))

                for obj in tile.findall('Object'):
                    if obj.get('id') != '15': # Exclude Pikachu
                        objects.add((x, y))
                        break

                if tile.find('Boulder') is not None:
                    objects.add((x, y))

        if map_id == '108' and (2, 1) in objects:
            objects.remove((2, 1))

        return objects

    def reconstruct_path(self, came_from, current):
        total_path = [current[0]]
        while current in came_from:
            current = came_from[current]
            total_path.append(current[0])
        path_json = [{'x': p[0], 'y': p[1]} for p in reversed(total_path)]
        return json.dumps(path_json)

pathfinder = AStar(map_xml_string)
pathfinder.find_path(input_data['target_x'], input_data['target_y'], input_data.get('ignorable_coords'), bool(input_data.get('is_surfing')))

