import xml.etree.ElementTree as ET
import json

root = ET.fromstring(map_xml_string)
impassable_coords = []
impassable_types = ['impassable', 'boulder_barrier', 'closed_gate']

for row in root.findall('Row'):
    y = int(row.get('id'))
    for tile in row.findall('Tile'):
        x = int(tile.get('id'))
        is_impassable = False
        if tile.get('type') in impassable_types:
            is_impassable = True
        else:
            for obj in tile.findall('Object'):
                if obj.get('id-name') != 'Pikachu':
                    is_impassable = True
                    break

        if is_impassable:
            impassable_coords.append({'x': x, 'y': y})

print(json.dumps(impassable_coords))

