import xml.etree.ElementTree as ET
import json

puzzle_elements = {
    "boulders": [],
    "switches": [],
    "barriers": []
}
root = ET.fromstring(map_xml_string)

for row in root.findall('Row'):
    y = int(row.get('id'))
    for tile in row.findall('Tile'):
        x = int(tile.get('id'))
        if tile.find('Boulder') is not None:
            puzzle_elements["boulders"].append({'x': x, 'y': y})
        if tile.find('BoulderSwitch') is not None:
            puzzle_elements["switches"].append({'x': x, 'y': y})
        if tile.find('BoulderBarrier') is not None:
            puzzle_elements["barriers"].append({'x': x, 'y': y})

print(json.dumps(puzzle_elements))
