import xml.etree.ElementTree as ET
import json
import collections

def solve():
    root = ET.fromstring(map_xml_string)
    width = int(root.attrib['width'])
    height = int(root.attrib['height'])

    grid = {}
    player_pos = None

    for row in root.findall('Row'):
        y = int(row.attrib['id'])
        for tile in row.findall('Tile'):
            x = int(tile.attrib['id'])
            t_type = tile.attrib['type']
            seen = tile.attrib['seen'] == 'true'
            has_player = tile.find('Player') is not None
            has_obj = tile.attrib.get('has-object') == 'true'

            grid[(x, y)] = {
                'type': t_type,
                'seen': seen,
                'has_object': has_obj
            }
            if has_player:
                player_pos = (x, y)

    if not player_pos:
        print(json.dumps([]))
        return

    queue = collections.deque([player_pos])
    visited = {player_pos}
    parent = {player_pos: None}
    reachable_unseen = []

    while queue:
        curr = queue.popleft()
        cx, cy = curr

        for dx, dy in [(0,1), (0,-1), (1,0), (-1,0)]:
            nx, ny = cx + dx, cy + dy
            if 0 <= nx < width and 0 <= ny < height:
                neighbor = (nx, ny)
                if neighbor not in visited:
                    tile = grid.get(neighbor)
                    if not tile: continue

                    if not tile['seen']:
                        reachable_unseen.append(neighbor)
                        visited.add(neighbor)
                        parent[neighbor] = curr
                    elif tile['type'] in ['FLOOR', 'LADDER', 'TALL_GRASS'] and not tile['has_object']:
                        visited.add(neighbor)
                        parent[neighbor] = curr
                        queue.append(neighbor)

    if not input_data.get('autopress_buttons', False):
        print(json.dumps([f"({x}, {y})" for x, y in sorted(reachable_unseen)]))
        return

    if not reachable_unseen:
        print(json.dumps([]))
        return

    target = reachable_unseen[0]
    path = []
    curr = target
    while curr != player_pos:
        prev = parent[curr]
        px, py = prev
        cx, cy = curr
        if cx > px: path.append("Right")
        elif cx < px: path.append("Left")
        elif cy > py: path.append("Down")
        elif cy < py: path.append("Up")
        curr = prev

    print(json.dumps(path[::-1]))

solve()
