import xml.etree.ElementTree as ET
import json
from collections import deque

def find_path(map_xml, end_x, end_y, start_x=None, start_y=None, can_surf=False):
    try:
        root = ET.fromstring(map_xml)
    except Exception as e:
        return f"Error parsing XML: {str(e)}"

    if start_x is None or start_y is None:
        player_found = False
        for row in root.findall('Row'):
            for tile in row.findall('Tile'):
                if tile.find('Player') is not None:
                    start_x = int(tile.attrib['id'])
                    start_y = int(row.attrib['id'])
                    player_found = True
                    break
            if player_found: break

    if start_x is None or start_y is None:
        return "Error: Player position not found."

    grid = {}
    passable_types = ["FLOOR", "TALL_GRASS", "WARP_CARPET_UP", "WARP_CARPET_DOWN", "WARP_CARPET_LEFT", "WARP_CARPET_RIGHT", "LADDER", "STAIRS", "DIRT", "SAND"]
    if can_surf:
        passable_types.append("WATER")

    for row in root.findall('Row'):
        y = int(row.attrib['id'])
        for tile in row.findall('Tile'):
            x = int(tile.attrib['id'])
            tile_type = tile.attrib.get('type', 'VOID')
            has_object = tile.attrib.get('has-object') == 'true'

            # Start and end tiles are treated as passable for the purpose of the search
            if (x == start_x and y == start_y) or (x == end_x and y == end_y):
                grid[(x, y)] = True
                continue

            is_passable = tile_type in passable_types and not has_object
            grid[(x, y)] = is_passable

    queue = deque([(start_x, start_y, [])])
    visited = set([(start_x, start_y)])

    while queue:
        curr_x, curr_y, path = queue.popleft()

        if curr_x == end_x and curr_y == end_y:
            buttons = []
            prev_x, prev_y = start_x, start_y
            for step_x, step_y in path:
                if step_x > prev_x: buttons.append("Right")
                elif step_x < prev_x: buttons.append("Left")
                elif step_y > prev_y: buttons.append("Down")
                elif step_y < prev_y: buttons.append("Up")
                prev_x, prev_y = step_x, step_y
            return buttons

        for dx, dy in [(0, 1), (0, -1), (1, 0), (-1, 0)]:
            nx, ny = curr_x + dx, curr_y + dy
            if (nx, ny) in grid and grid[(nx, ny)] and (nx, ny) not in visited:
                visited.add((nx, ny))
                queue.append((nx, ny, path + [(nx, ny)]))

    return "Error: No path found to the destination."

try:
