import xml.etree.ElementTree as ET
import heapq
import json

def to_buttons(path):
    buttons = []
    for i in range(len(path) - 1):
        current_pos = path[i]
        next_pos = path[i+1]
        if next_pos[0] > current_pos[0]:
            buttons.append("Right")
        elif next_pos[0] < current_pos[0]:
            buttons.append("Left")
        elif next_pos[1] > current_pos[1]:
            buttons.append("Down")
        elif next_pos[1] < current_pos[1]:
            buttons.append("Up")
    return buttons

try:
    start_x = int(input_data['start_x'])
    start_y = int(input_data['start_y'])
    end_x = int(input_data['end_x'])
    end_y = int(input_data['end_y'])
    traversable_tiles_str = input_data.get('traversable_tiles', 'FLOOR,GRASS,TALL_GRASS,LONG_GRASS,RAILING,PIPE_HORIZONTAL,PIPE_VERTICAL,WARP_CARPET_DOWN,WARP_CARPET_LEFT,WARP_CARPET_RIGHT,unknown')
    traversable_tiles = {tile.strip() for tile in traversable_tiles_str.split(',')}

    root = ET.fromstring(map_xml_string)
    width = int(root.attrib['width'])
    height = int(root.attrib['height'])

    tile_data = {}
    for row_elem in root.findall('Row'):
        y = int(row_elem.attrib['id'])
        for tile_elem in row_elem.findall('Tile'):
            x = int(tile_elem.attrib['id'])
            tile_type = tile_elem.attrib['type']
            has_object = tile_elem.attrib.get('has-object') == 'true'
            tile_data[(x, y)] = {'type': tile_type, 'has_object': has_object}

    start_node = (start_x, start_y)
    target_node = (end_x, end_y)

    actual_end_node = None
    end_node_info = tile_data.get(target_node)

    if end_node_info and end_node_info['type'] in traversable_tiles and not end_node_info['has_object']:
        actual_end_node = target_node
    else:
        potential_ends = []
        for dx, dy in [(0, 1), (0, -1), (1, 0), (-1, 0)]:
            adj_node = (target_node[0] + dx, target_node[1] + dy)
            adj_info = tile_data.get(adj_node)
            if adj_info and adj_info['type'] in traversable_tiles and not adj_info['has_object']:
                dist = abs(adj_node[0] - start_node[0]) + abs(adj_node[1] - start_node[1])
                heapq.heappush(potential_ends, (dist, adj_node))

        if potential_ends:
            _, actual_end_node = heapq.heappop(potential_ends)

    if actual_end_node is None:
        print(json.dumps({"error": "No path found. Destination and all adjacent tiles are unreachable."}))
        exit()

    open_set = []
    heapq.heappush(open_set, (0, start_node))
    came_from = {}
    g_score = {start_node: 0}
    f_score = {start_node: abs(start_node[0] - actual_end_node[0]) + abs(start_node[1] - actual_end_node[1])}

    path_found = False
    while open_set:
        _, current = heapq.heappop(open_set)

        if current == actual_end_node:
            path = []
            temp = current
            while temp in came_from:
                path.append(temp)
                temp = came_from[temp]
            path.append(start_node)
            path.reverse()
            buttons = to_buttons(path)
            print(json.dumps({"path": buttons}))
            path_found = True
            break

        for dx, dy in [(0, 1), (0, -1), (1, 0), (-1, 0)]:
            neighbor = (current[0] + dx, current[1] + dy)

            if not (1 <= neighbor[0] <= width and 1 <= neighbor[1] <= height):
                continue

            neighbor_info = tile_data.get(neighbor)
            current_info = tile_data.get(current)

            if not neighbor_info or neighbor_info['has_object']:
                continue

            if neighbor_info['type'] not in traversable_tiles:
                continue

            if neighbor_info['type'] == 'LEDGE_HOP_DOWN' and dy != 1: continue
            if neighbor_info['type'] == 'LEDGE_HOP_LEFT' and dx != -1: continue
            if neighbor_info['type'] == 'LEDGE_HOP_RIGHT' and dx != 1: continue
            if current_info and current_info['type'] == 'LEDGE_HOP_DOWN' and dy == -1: continue
            if current_info and current_info['type'] == 'LEDGE_HOP_LEFT' and dx == 1: continue
            if current_info and current_info['type'] == 'LEDGE_HOP_RIGHT' and dx == -1: continue

            tentative_g_score = g_score.get(current, float('inf')) + 1
            if tentative_g_score < g_score.get(neighbor, float('inf')):
                came_from[neighbor] = current
                g_score[neighbor] = tentative_g_score
                f_score[neighbor] = tentative_g_score + abs(neighbor[0] - actual_end_node[0]) + abs(neighbor[1] - actual_end_node[1])
                if neighbor not in [i[1] for i in open_set]:
                    heapq.heappush(open_set, (f_score[neighbor], neighbor))

    if not path_found:
        print(json.dumps({"error": "No path found."}))
except Exception as e:
    print(json.dumps({"error": f"An error occurred: {e}"}))
