import xml.etree.ElementTree as ET
from collections import deque
import json

def run():
    root = ET.fromstring(map_xml_string)
    map_id = root.attrib.get('id')
    start = (int(input_data['start_x']), int(input_data['start_y']))
    end = (int(input_data['end_x']), int(input_data['end_y']))
    autopress = input_data.get('autopress_buttons', False)

    grid = {}
    for row in root.findall('Row'):
        y = int(row.attrib['id'])
        for tile in row.findall('Tile'):
            x = int(tile.attrib['id'])
            t_type = tile.attrib.get('type')
            is_passable = t_type in ['FLOOR', 'LADDER', 'WARP_CARPET_DOWN', 'WARP_CARPET_UP', 'WARP_CARPET_LEFT', 'WARP_CARPET_RIGHT', 'PIT']

            obj = tile.find('Object')
            has_obj = tile.attrib.get('has-object') == 'true' or obj is not None

            if has_obj:
                is_passable = False
                # Blackthorn Gym Bridge Exception: Boulders on 1F are passable
                if map_id == '5_1':
                    if obj is not None and 'BOULDER' in obj.attrib.get('id-name', '').upper():
                        is_passable = True
                    elif obj is None and tile.attrib.get('has-object') == 'true':
                        is_passable = True
                # General Pit/Warp Exception (for 2F navigation)
                if t_type == 'PIT' or tile.attrib.get('is-warp') == 'true':
                    if obj is not None and 'BOULDER' in obj.attrib.get('id-name', '').upper():
                        is_passable = True

            if (x, y) == end:
                is_passable = True
            grid[(x, y)] = is_passable

    queue = deque([(start, [])])
    visited = {start}

    while queue:
        (x, y), path = queue.popleft()
        if (x, y) == end:
            if autopress:
                print(json.dumps(path))
            else:
                coord_path = [{"x": start[0], "y": start[1]}]
                cx, cy = start
                for btn in path:
                    if btn == "Up": cy -= 1
                    elif btn == "Down": cy += 1
                    elif btn == "Left": cx -= 1
                    elif btn == "Right": cx += 1
                    coord_path.append({"x": cx, "y": cy})
                print(json.dumps(coord_path))
            return

        for dx, dy, btn in [(0, 1, "Down"), (0, -1, "Up"), (1, 0, "Right"), (-1, 0, "Left")]:
            nx, ny = x + dx, y + dy
            if (nx, ny) in grid and grid[(nx, ny)] and (nx, ny) not in visited:
                visited.add((nx, ny))
                queue.append(((nx, ny), path + [btn]))
    print(json.dumps([]))

run()

solve_gym_puzzle.python_script:
import xml.etree.ElementTree as ET
import json
from collections import deque

def run():
    try:
        root = ET.fromstring(map_xml_string)
        map_elem = root if root.attrib.get('id') == '5_2' else root.find(".//Map[@id='5_2']")

        passable = set()
        pits = set()
        boulders = {}
        walls = set()

        for row in map_elem.findall('Row'):
            y = int(row.attrib['id'])
            for tile in row.findall('Tile'):
                x = int(tile.attrib['id'])
                t = tile.attrib['type']

                if t in ('FLOOR', 'LADDER'):
                    passable.add((x, y))
                elif t == 'PIT':
                    pits.add((x, y))
                elif t == 'WALL':
                    walls.add((x, y))

                # Check for objects
                obj = tile.find('Object')
                if obj is not None:
                    name = obj.attrib.get('id-name', '').upper()
                    oid = int(obj.attrib.get('id', 0))
                    if 'BOULDER' in name:
                        boulders[oid] = (x, y)
                    elif 'COOLTRAINER' in name:
                        walls.add((x, y))

                # Use markers for off-screen boulders and defeated trainers
                for marker in tile.findall('Marker'):
                    label = marker.text.upper()
                    if 'BOULDER' in label:
                        oid = int(marker.attrib.get('object_id', 99))
                        if oid not in boulders:
                            boulders[oid] = (x, y)
                    elif 'DEFEATED' in label:
                        walls.add((x, y))

        start_p = (int(input_data['start_x']), int(input_data['start_y']))
        start_b = tuple(sorted(boulders.values()))

        # Target: fill all three pits (2, 5), (8, 3), and (8, 7). 
        # Since we have 3 boulders and 3 pits, the goal is for all boulders to be in pits.
        target_pits = {(2, 5), (8, 3), (8, 7)}

        queue = deque([(start_p, start_b, [])])
        visited = { (start_p, start_b) }

        while queue: 
            cp, cb_t, path = queue.popleft()

            # Success if all boulders are gone (meaning they all fell into pits)
            if len(cb_t) == 0:
                print(json.dumps(path))
                return

            if len(path) > 400: continue

            for dx, dy, btn in [(0, -1, 'Up'), (0, 1, 'Down'), (-1, 0, 'Left'), (1, 0, 'Right')]:
                nx, ny = cp[0] + dx, cp[1] + dy
                if (nx, ny) in cb_t:
                    bx, by = nx + dx, ny + dy
                    # Boulder can move to floor or one of the target pits
                    if (bx, by) in (passable | target_pits) and (bx, by) not in cb_t and (bx, by) not in walls:
                        new_b_list = list(cb_t)
                        new_b_list.remove((nx, ny))
                        if (bx, by) not in target_pits:
                            new_b_list.append((bx, by))
                        new_b_t = tuple(sorted(new_b_list))
                        if (nx, ny, new_b_t) not in visited:
                            visited.add((nx, ny, new_b_t))
                            queue.append(((nx, ny), new_b_t, path + [btn]))
                elif (nx, ny) in passable and (nx, ny) not in walls:
                    if (nx, ny, cb_t) not in visited:
                        visited.add((nx, ny, cb_t))
                        queue.append(((nx, ny), cb_t, path + [btn]))
        print(json.dumps({"error": "No solution found"}))
    except Exception as e:
        print(json.dumps({"error": str(e)}))
run()
