import xml.etree.ElementTree as ET
from collections import deque
import json

def solve_path():
    try:
        root = ET.fromstring(map_xml_string)
        width = int(root.get('width'))
        height = int(root.get('height'))

        # Parse Grid
        grid = {}
        for row in root.findall('Row'):
            y = int(row.get('id'))
            for tile in row.findall('Tile'):
                x = int(tile.get('id'))
                t_type = tile.get('type')

                is_blocked = False

                # Type blocking
                if mode == 'surfing':
                     if t_type not in ['WATER']:
                         is_blocked = True
                else: # walking
                     if t_type in ['WATER', 'void', 'unseen']:
                         is_blocked = True
                     elif 'WALL' in t_type:
                         is_blocked = True

                # Explicit obstacles
                if tile.get('has-object') == 'true':
                    is_blocked = True
                if tile.find('Object') is not None:
                    is_blocked = True

                # Markers with object_id
                for marker in tile.findall('Marker'):
                    if marker.get('object_id'):
                        is_blocked = True
                        break

                grid[(x, y)] = {'type': t_type, 'blocked': is_blocked}

        # BFS
        queue = deque([(start_x, start_y, [])])
        visited = set([(start_x, start_y)])

        while queue:
            cx, cy, path = queue.popleft()

            if cx == target_x and cy == target_y:
                print(json.dumps(path))
                return

            moves = [('Up', 0, -1), ('Down', 0, 1), ('Left', -1, 0), ('Right', 1, 0)]

            for move_name, dx, dy in moves:
                nx, ny = cx + dx, cy + dy

                if not (0 <= nx < width and 0 <= ny < height):
                    continue

                target_tile = grid.get((nx, ny))
                if not target_tile: continue

                t_type = target_tile['type']
                is_ledge = 'LEDGE' in t_type

                landing_x, landing_y = nx, ny

                if is_ledge:
                    if target_tile['blocked']: continue

                    valid_ledge = False
                    if t_type == 'LEDGE_HOP_DOWN' and move_name == 'Down':
                        landing_x, landing_y = nx, ny + 1
                        valid_ledge = True
                    elif t_type == 'LEDGE_HOP_LEFT' and move_name == 'Left':
                        landing_x, landing_y = nx - 1, ny
                        valid_ledge = True
                    elif t_type == 'LEDGE_HOP_RIGHT' and move_name == 'Right':
                        landing_x, landing_y = nx + 1, ny
                        valid_ledge = True

                    if not valid_ledge:
                        continue 
                else:
                    if target_tile['blocked']:
                        continue

                if not (0 <= landing_x < width and 0 <= landing_y < height):
                    continue

                landing_tile = grid.get((landing_x, landing_y))
                if not landing_tile or landing_tile['blocked']:
                    continue

                if (landing_x, landing_y) in visited:
                    continue

                visited.add((landing_x, landing_y))
                if is_ledge: visited.add((nx, ny))

                queue.append((landing_x, landing_y, path + [move_name]))

        print(json.dumps([]))
    except Exception:
        print(json.dumps([]))

solve_path()

