import json

try:
    target = int(input_data.get('target_slot', 1))
    current = int(input_data.get('current_slot', 1))

    buttons = []
    # Select FIGHT
    buttons.append("A")
    buttons.append("sleep 500")

    # Navigate
    added_nav = False
    if current == 1:
        if target == 1: pass
        elif target == 2: 
            buttons.append("Down")
            added_nav = True
        elif target == 3: 
            buttons.extend(["Down", "Down"])
            added_nav = True
        elif target == 4: 
            buttons.append("Up")
            added_nav = True
    elif current == 2:
        if target == 1: 
            buttons.append("Up")
            added_nav = True
        elif target == 2: pass
        elif target == 3: 
            buttons.append("Down")
            added_nav = True
        elif target == 4: 
            buttons.extend(["Down", "Down"])
            added_nav = True
    elif current == 3:
        if target == 1: 
            buttons.extend(["Up", "Up"])
            added_nav = True
        elif target == 2: 
            buttons.append("Up")
            added_nav = True
        elif target == 3: pass
        elif target == 4: 
            buttons.append("Down")
            added_nav = True
    elif current == 4:
        if target == 1: 
            buttons.append("Down")
            added_nav = True
        elif target == 2: 
            buttons.extend(["Down", "Down"])
            added_nav = True
        elif target == 3: 
            buttons.append("Up")
            added_nav = True
        elif target == 4: pass

    # Avoid consecutive sleeps
    if added_nav:
        buttons.append("sleep 250")

    buttons.append("A") # Select Move

    print(json.dumps(buttons))
except Exception as e:
    # Fallback to simple A press if logic fails
    print(json.dumps(["A", "sleep 500", "A"]))
