import sys
import json
import xml.etree.ElementTree as ET
from collections import deque

def bfs_path(start_x, start_y, target_x, target_y, mode, map_xml):
    try:
        root = ET.fromstring(map_xml)
        width = int(root.attrib['width'])
        height = int(root.attrib['height'])

        grid = {} # (x,y) -> is_walkable

        for row in root.findall('Row'):
            y = int(row.attrib['id'])
            for tile in row.findall('Tile'):
                x = int(tile.attrib['id'])
                t_type = tile.attrib.get('type', 'FLOOR')
                seen = tile.attrib.get('seen', 'false') == 'true'
                has_object = tile.attrib.get('has-object', 'false') == 'true'

                is_walkable = True

                # Check Obstacles
                if has_object:
                    is_walkable = False
                if t_type == 'WALL':
                    is_walkable = False

                # Mode specific
                if mode == 'walking':
                    if t_type == 'WATER':
                        is_walkable = False
                    if not seen:
                         is_walkable = False
                elif mode == 'surfing':
                    # Surfing requires water (or transitions which we simplify here)
                    if t_type != 'WATER' and t_type != 'LADDER' and not (x == start_x and y == start_y):
                        is_walkable = False
                    if not seen:
                        is_walkable = False

                grid[(x,y)] = is_walkable

        # Ensure start is valid enough to begin
        if (start_x, start_y) not in grid: grid[(start_x, start_y)] = True 

        queue = deque([(start_x, start_y, [])])
        visited = set([(start_x, start_y)])

        while queue:
            cx, cy, path = queue.popleft()

            if cx == target_x and cy == target_y:
                return path

            for dx, dy, move in [(0, -1, "Up"), (0, 1, "Down"), (-1, 0, "Left"), (1, 0, "Right")]:
                nx, ny = cx + dx, cy + dy

                if (nx, ny) in grid and grid[(nx, ny)]:
                    if (nx, ny) not in visited:
                        visited.add((nx, ny))
                        queue.append((nx, ny, path + [move]))

        return [] # No path found
    except Exception:
        return []

