import xml.etree.ElementTree as ET
from collections import deque
import json

def solve():
    try:
        target_x = int(input_data['target_x'])
        target_y = int(input_data['target_y'])
        surfing = True

        root = ET.fromstring(map_xml_string)

        grid = {}
        player_pos = None
        for row in root.findall('Row'):
            y = int(row.attrib['id'])
            for tile in row.findall('Tile'):
                x = int(tile.attrib['id'])
                tile_type = tile.attrib['type']
                has_object = tile.attrib.get('has-object') == 'true'
                is_warp = tile.attrib.get('is-warp') == 'true'
                if tile.find('Player') is not None:
                    player_pos = (x, y)

                passable = True
                if tile_type in ['WALL', 'BUOY', 'ROCK', 'BOOKSHELF', 'TV', 'RADIO', 'TOWN_MAP', 'STATUE', 'COUNTER', 'BOULDER', 'PC', 'PIT', 'WHIRLPOOL']:
                    passable = False
                elif tile_type == 'WATER' and not surfing:
                    passable = False
                elif tile_type == 'unseen':
                    passable = False

                if has_object:
                    if tile.find('Player') is None:
                        passable = False

                if is_warp and (x, y) != (target_x, target_y):
                    passable = False

                if (x, y) == (target_x, target_y):
                    passable = True

                grid[(x, y)] = passable

        if player_pos is None:
            print("[]")
            return

        start_x, start_y = player_pos
        queue = deque([(start_x, start_y, [{"x": start_x, "y": start_y}])])
        visited = set([(start_x, start_y)])

        while queue:
            x, y, path = queue.popleft()
            if (x, y) == (target_x, target_y):
                print(json.dumps(path))
                return

            for dx, dy in [(0, 1), (0, -1), (1, 0), (-1, 0)]:
                nx, ny = x + dx, y + dy
                if (nx, ny) in grid and grid[(nx, ny)] and (nx, ny) not in visited:
                    visited.add((nx, ny))
                    queue.append((nx, ny, path + [{"x": nx, "y": ny}]))
        print("[]")
    except Exception as e:
        print(json.dumps([{"error": str(e)}]))

solve()
