import json
import xml.etree.ElementTree as ET
from collections import deque
import sys

def solve():
    try:
        # Use input_data injected by the harness
        start_x = int(input_data['start_x'])
        start_y = int(input_data['start_y'])
        target_x = int(input_data['target_x'])
        target_y = int(input_data['target_y'])
        autopress = input_data.get('autopress_buttons', False)

        root = ET.fromstring(map_xml_string)
        width = int(root.attrib['width'])
        height = int(root.attrib['height'])

        grid = {}
        # Pre-fill with assumption that unseen are walkable (for exploration)
        for y in range(height):
            for x in range(width):
                grid[(x, y)] = True

        for row in root.findall('Row'):
            y = int(row.attrib['id'])
            for tile in row.findall('Tile'):
                x = int(tile.attrib['id'])
                t_type = tile.attrib['type']
                has_obj = tile.attrib.get('has-object') == 'true'
                seen = tile.attrib['seen'] == 'true'

                if seen:
                    if t_type in ['WALL', 'BUOY', 'COUNTER', 'HEADBUTT_TREE', 'CUT_TREE', 'FRUIT_TREE']:
                        grid[(x, y)] = False
                    elif t_type == 'FLOOR_UP_WALL':
                        grid[(x, y)] = 'UP_WALL'
                    elif t_type == 'LEDGE_HOP_DOWN':
                        grid[(x, y)] = 'LEDGE'
                    else:
                        grid[(x, y)] = True

                    if has_obj:
                        # Player tile is always walkable
                        if not tile.find('Player') is not None:
                            grid[(x, y)] = False

        queue = deque([((start_x, start_y), [])])
        visited = {(start_x, start_y)}

        while queue:
            (cx, cy), path = queue.popleft()

            if (cx, cy) == (target_x, target_y):
                res_path = path + [{"x": cx, "y": cy}]
                if autopress:
                    # Convert path to buttons
                    buttons = []
                    for i in range(len(res_path)-1):
                        p1 = res_path[i]
                        p2 = res_path[i+1]
                        if p2['x'] > p1['x']: buttons.append('Right')
                        elif p2['x'] < p1['x']: buttons.append('Left')
                        elif p2['y'] > p1['y']: buttons.append('Down')
                        elif p2['y'] < p1['y']: buttons.append('Up')
                    print(json.dumps(buttons[:50]))
                else:
                    print(json.dumps(res_path))
                return

            for dx, dy in [(0, 1), (0, -1), (1, 0), (-1, 0)]:
                nx, ny = cx + dx, cy + dy

                if (nx, ny) in grid:
                    val = grid[(nx, ny)]
                    if val is False: continue

                    # Crystal Collision Rules
                    # 1. UP_WALL: Blocks movement across the top edge (entry from North, exit to North)
                    if dy == 1 and val == 'UP_WALL': continue # Moving SOUTH into UP_WALL
                    if dy == -1 and grid.get((cx, cy)) == 'UP_WALL': continue # Moving NORTH out of UP_WALL
                    if dy == -1 and val == 'UP_WALL': continue # Moving NORTH into UP_WALL

                    # 2. LEDGE_HOP_DOWN: One-way jump from North to South (blocks North entry)
                    if dy == -1 and val == 'LEDGE': continue # Moving NORTH into LEDGE

                    if (nx, ny) not in visited:
                        visited.add((nx, ny))
                        queue.append(((nx, ny), path + [{"x": cx, "y": cy}]))

        print('[]')

    except Exception as e:
        print(json.dumps({"error": str(e)}))

if __name__ == "__main__":
    solve()
