import heapq
import xml.etree.ElementTree as ET
import json

def heuristic(a, b):
    return abs(a[0] - b[0]) + abs(a[1] - b[1])

def a_star_search(start, goal, traversable, one_way_rules):
    open_set = [(0 + heuristic(start, goal), 0, start)]
    came_from = {}
    g_score = {start: 0}

    while open_set:
        _, current_g, current_node = heapq.heappop(open_set)

        if current_node == goal:
            return True # Path found

        for dx, dy in [(0, 1), (0, -1), (1, 0), (-1, 0)]:
            neighbor = (current_node[0] + dx, current_node[1] + dy)

            if neighbor not in traversable and neighbor != goal: # Allow pathing to the goal even if it's not in the initial traversable set (e.g., unseen)
                continue

            if neighbor in one_way_rules:
                allowed_from = one_way_rules[neighbor]
                if (current_node[0] - neighbor[0], current_node[1] - neighbor[1]) != allowed_from:
                    continue

            tentative_g_score = current_g + 1

            if neighbor not in g_score or tentative_g_score < g_score[neighbor]:
                g_score[neighbor] = tentative_g_score
                f_score = tentative_g_score + heuristic(neighbor, goal)
                heapq.heappush(open_set, (f_score, tentative_g_score, neighbor))
                came_from[neighbor] = current_node

    return False # No path found

