import xml.etree.ElementTree as ET
import heapq
import json

def find_path(target_x, target_y, party_data_str):
    # XML parsing
    root = ET.fromstring(map_xml_string)
    width = int(root.attrib['width'])
    height = int(root.attrib['height'])
    grid = [[None for _ in range(width)] for _ in range(height)]
    player_pos = None

    for r_idx, row_elem in enumerate(root.findall('Row')):
        for t_idx, tile_elem in enumerate(row_elem.findall('Tile')):
            grid[r_idx][t_idx] = tile_elem
            if tile_elem.find('Player') is not None:
                player_pos = (t_idx, r_idx)

    if player_pos is None:
        print("Error: Player position not found.")
        return

    party = json.loads(party_data_str)

    def has_hm(hm_name):
        return any(hm_name in move['name'] for p in party for move in p.get('moves', []) if not p.get('fainted', True))

    def is_walkable(x, y, from_x, from_y):
        if not (0 <= x < width and 0 <= y < height):
            return False
        tile = grid[y][x]
        tile_type = tile.attrib.get('type')
        from_tile = grid[from_y][from_x]
        from_tile_type = from_tile.attrib.get('type')

        if tile.find('Object') is not None and tile.find('Object').attrib.get('id-name') != 'pikachu':
            return False

        walkable_types = ['ground', 'grass', 'cleared_boulder_barrier', 'open_gate', 'gate_offscreen', 'teleport', 'hole', 'ladder_up', 'ladder_down']
        if tile_type in walkable_types:
            return True
        if tile_type == 'water' and from_tile_type == 'water':
            return has_hm('SURF')
        if tile_type == 'cuttable':
            return has_hm('CUT')
        if tile_type == 'steps':
            return True
        if tile_type == 'ledge' and y > from_y:
            return True
        return False

    def get_neighbors(x, y):
        neighbors = []
        for dx, dy, move in [(0, -1, 'Up'), (0, 1, 'Down'), (-1, 0, 'Left'), (1, 0, 'Right')]:
            nx, ny = x + dx, y + dy
            if is_walkable(nx, ny, x, y):
                cost = 1
                tile = grid[ny][nx]
                if tile.attrib.get('type') == 'ledge' and dy == 1:
                    if is_walkable(nx, ny + 1, nx, ny):
                        neighbors.append(((nx, ny + 1), move, cost))
                else:
                    neighbors.append(((nx, ny), move, cost))
        return neighbors

    target_tile = grid[target_y][target_x]
    if not is_walkable(target_x, target_y, target_x, target_y):
        found_adjacent = False
        for dx, dy, _ in [(0, -1, 'Up'), (0, 1, 'Down'), (-1, 0, 'Left'), (1, 0, 'Right')]:
            ax, ay = target_x + dx, target_y + dy
            if is_walkable(ax, ay, target_x, target_y):
                target_x, target_y = ax, ay
                found_adjacent = True
                break
        if not found_adjacent:
            print(f"Error: Target ({target_x},{target_y}) and its adjacent tiles are unwalkable.")
            return

    start_node = (player_pos[0], player_pos[1])
    target_node = (target_x, target_y)
    open_set = [(0, start_node, [])]
    closed_set = set()

    while open_set:
        cost, current, path = heapq.heappop(open_set)
        if current == target_node:
            print(json.dumps(path + [current]))
            return
        if current in closed_set:
            continue
        closed_set.add(current)
        for neighbor, move, move_cost in get_neighbors(current[0], current[1]):
            if neighbor not in closed_set:
                new_cost = cost + move_cost
                new_path = path + [current]
                heapq.heappush(open_set, (new_cost, neighbor, new_path))
    print("Error: No path found.")

find_path(int(input_data['target_x']), int(input_data['target_y']), input_data['party_data'])

spinner_maze_solver.python_script:
import xml.etree.ElementTree as ET
import heapq

def spinner_maze_solver(input_data, map_xml_string):
    try:
        target_x = int(input_data['target_x'])
        target_y = int(input_data['target_y'])
        root = ET.fromstring(map_xml_string)
    except (ValueError, ET.ParseError):
        return 'Error: Invalid input or map data.'

    tile_map = {}
    player_pos = None
    width = int(root.get('width'))
    height = int(root.get('height'))

    for row_elem in root.findall('Row'):
        y = int(row_elem.get('id'))
        for tile_elem in row_elem.findall('Tile'):
            x = int(tile_elem.get('id'))
            tile_type = tile_elem.get('type')
            has_obj = tile_elem.find('Object') is not None
            obj_is_pikachu = False
            if has_obj:
                obj_elem = tile_elem.find('Object')
                if obj_elem.get('id-name') == 'Pikachu':
                    obj_is_pikachu = True

            spinner_info = tile_elem.find('Spinner')
            spinner_direction = spinner_info.get('direction') if spinner_info is not None else None
            tile_map[(x, y)] = {'type': tile_type, 'has_obj': has_obj, 'obj_is_pikachu': obj_is_pikachu, 'spinner': spinner_direction}

            if tile_elem.find('Player') is not None:
                player_pos = (x, y)

    if not player_pos:
        return 'Error: Player position not found.'

    def is_walkable(coord, ignore_spinner=False):
        if coord not in tile_map: return False
        tile = tile_map[coord]
        if tile['has_obj'] and not tile['obj_is_pikachu']: return False
        walkable_types = ['ground', 'grass', 'steps', 'cleared_boulder_barrier', 'open_gate', 'gate_offscreen', 'teleport', 'ladder_up', 'ladder_down', 'spinner_stop']
        if not ignore_spinner and tile['spinner']:
             walkable_types.extend(['spinner_up', 'spinner_down', 'spinner_left', 'spinner_right'])
        return tile['type'] in walkable_types

    def get_spinner_destination(start_coord):
        x, y = start_coord
        path = [start_coord]
        visited_spinners = {start_coord}
        while True:
            if (x, y) not in tile_map or not tile_map[(x, y)]['spinner']:
                return (x, y), path

            direction = tile_map[(x, y)]['spinner']
            if direction == 'up': y -= 1
            elif direction == 'down': y += 1
            elif direction == 'left': x -= 1
            elif direction == 'right': x += 1

            if (x, y) in visited_spinners: return None, None # Infinite loop
            visited_spinners.add((x, y))
            path.append((x,y))

            if not (1 <= x <= width and 1 <= y <= height):
                 return None, None # Out of bounds

    target_node = (target_x, target_y)
    is_target_impassable = not is_walkable(target_node, ignore_spinner=True)

    def heuristic(a, b):
        return abs(a[0] - b[0]) + abs(a[1] - b[1])

    open_set = [(0, player_pos)]
    came_from = {}
    g_score = {player_pos: 0}

    while open_set:
        _, current = heapq.heappop(open_set)

        if not is_target_impassable and current == target_node:
            break
        if is_target_impassable and heuristic(current, target_node) == 1 and is_walkable(current):
            break

        # Standard moves
        for dx, dy in [(0, 1), (0, -1), (1, 0), (-1, 0)]:
            neighbor = (current[0] + dx, current[1] + dy)
            if not is_walkable(neighbor): continue

            tentative_g_score = g_score[current] + 1
            if neighbor not in g_score or tentative_g_score < g_score[neighbor]:
                g_score[neighbor] = tentative_g_score
                f_score = tentative_g_score + heuristic(neighbor, target_node)
                heapq.heappush(open_set, (f_score, neighbor))
                came_from[neighbor] = current

        # Spinner move
        if tile_map[current]['spinner']:
            dest, spin_path = get_spinner_destination(current)
            if dest and is_walkable(dest):
                tentative_g_score = g_score[current] + len(spin_path) # Cost is path length
                if dest not in g_score or tentative_g_score < g_score[dest]:
                    g_score[dest] = tentative_g_score
                    f_score = tentative_g_score + heuristic(dest, target_node)
                    heapq.heappush(open_set, (f_score, dest))
                    came_from[dest] = current # The start of the spin sequence

    else:
        return 'No path found.'

    # Reconstruct path
    path = []
    temp_current = current
    while temp_current in came_from:
        path.append(temp_current)
        temp_current = came_from[temp_current]
    path.append(player_pos)
    path.reverse()

    # Expand spinner sequences in the final path
    final_path = []
    for i in range(len(path) - 1):
        final_path.append(path[i])
        if tile_map[path[i]]['spinner'] and came_from.get(path[i+1]) == path[i]:
            _, spin_path = get_spinner_destination(path[i])
            # Exclude start and end as they are already in the main path
            if spin_path and len(spin_path) > 2:
                final_path.extend(spin_path[1:-1])
    final_path.append(path[-1])

    return final_path

print(spinner_maze_solver(input_data, map_xml_string))
