import json
import heapq

def heuristic(a, b):
    return abs(a['x'] - b['x']) + abs(a['y'] - b['y'])

def a_star_search(graph, start_node_id, end_node_id):
    nodes_by_id = {n['id']: n for n in graph['nodes']}
    start_node = nodes_by_id.get(start_node_id)
    end_node = nodes_by_id.get(end_node_id)
    if not start_node or not end_node:
        return None

    open_set = [(0, start_node_id)]
    came_from = {}
    g_score = {node_id: float('inf') for node_id in nodes_by_id}
    g_score[start_node_id] = 0
    f_score = {node_id: float('inf') for node_id in nodes_by_id}
    f_score[start_node_id] = heuristic(start_node['coordinates'], end_node['coordinates'])
    open_set_hash = {start_node_id}

    while open_set:
        _, current_id = heapq.heappop(open_set)
        open_set_hash.remove(current_id)

        if current_id == end_node_id:
            path = []
            temp = current_id
            while temp in came_from:
                path.append(temp)
                temp = came_from[temp]
            path.append(start_node_id)
            return path[::-1]

        for edge in graph['edges']:
            neighbor_id = None
            if edge['source_node_id'] == current_id:
                neighbor_id = edge['destination_node_id']
            elif edge['destination_node_id'] == current_id and not edge.get('is_one_way', False):
                neighbor_id = edge['source_node_id']

            if neighbor_id:
                tentative_g_score = g_score[current_id] + 1
                if tentative_g_score < g_score[neighbor_id]:
                    came_from[neighbor_id] = current_id
                    g_score[neighbor_id] = tentative_g_score
                    neighbor_node = nodes_by_id.get(neighbor_id)
                    if neighbor_node:
                        f_score[neighbor_id] = g_score[neighbor_id] + heuristic(neighbor_node['coordinates'], end_node['coordinates'])
                        if neighbor_id not in open_set_hash:
                            heapq.heappush(open_set, (f_score[neighbor_id], neighbor_id))
                            open_set_hash.add(neighbor_id)
    return None

try:
    world_graph = json.loads(world_knowledge_graph_json_string)
    start_map_id = input_data['start_map_id']
    start_x = int(input_data['start_x'])
    start_y = int(input_data['start_y'])
    end_map_id = input_data['end_map_id']
    end_x = int(input_data['end_x'])
    end_y = int(input_data['end_y'])

    start_nodes_on_map = [n for n in world_graph.get('nodes', []) if n.get('map_id') == start_map_id]
    end_nodes_on_map = [n for n in world_graph.get('nodes', []) if n.get('map_id') == end_map_id]

    if not start_nodes_on_map or not end_nodes_on_map:
        print(json.dumps({'path_found': False, 'path': [], 'error': 'Start or end map not found in World Knowledge Graph.'}))
    else:
        start_node = min(start_nodes_on_map, key=lambda n: heuristic(n['coordinates'], {'x': start_x, 'y': start_y}))
        end_node = min(end_nodes_on_map, key=lambda n: heuristic(n['coordinates'], {'x': end_x, 'y': end_y}))
        path_node_ids = a_star_search(world_graph, start_node['id'], end_node['id'])

        if path_node_ids:
            path_details = []
            for node_id in path_node_ids:
                node = next((n for n in world_graph['nodes'] if n['id'] == node_id), None)
