import xml.etree.ElementTree as ET; from collections import deque; import json; import traceback

def get_tile(root, x, y):
    for row in root.findall('Row'):
        if row.get('id') == str(y):
            for tile in row.findall('Tile'):
                if tile.get('id') == str(x):
                    return tile
    return None

def is_traversable(tile):
    if tile is None: return False
    tile_type = tile.get('type')
    if tile.find('Object') is not None:
        if tile.find('Object').get('id-name') == 'Pikachu': return True
        return False
    traversable_types = ['ground', 'grass', 'cleared_boulder_barrier', 'open_gate', 'teleport', 'hole', 'ladder_up', 'ladder_down', 'steps', 'spinner_up', 'spinner_down', 'spinner_left', 'spinner_right', 'spinner_stop']
    return tile_type in traversable_types

def analyze_segment(map_xml_string, start_x, start_y):
    try:
        root = ET.fromstring(map_xml_string)
        start_x, start_y = int(start_x), int(start_y)
        queue = deque([(start_x, start_y)])
        visited = set([(start_x, start_y)])
        reachable_tiles, reachable_warps, reachable_objects = [], [], []

        while queue:
            cx, cy = queue.popleft()
            current_tile_node = get_tile(root, cx, cy)
            if current_tile_node is None: continue

            reachable_tiles.append({'x': cx, 'y': cy})
            if current_tile_node.get('is-warp') == 'true': reachable_warps.append({'x': cx, 'y': cy})
            obj_node = current_tile_node.find('Object')
            if obj_node is not None: reachable_objects.append({'id': obj_node.get('id'), 'id-name': obj_node.get('id-name'), 'name': obj_node.get('name'), 'x': cx, 'y': cy})

            for dx, dy in [(0, 1), (0, -1), (1, 0), (-1, 0)]:
                nx, ny = cx + dx, cy + dy
                if (nx, ny) not in visited:
                    neighbor_tile = get_tile(root, nx, ny)
                    if is_traversable(neighbor_tile):
                        visited.add((nx, ny))
                        queue.append((nx, ny))

        result = {"reachable_tiles_count": len(reachable_tiles), "reachable_warps": reachable_warps, "reachable_objects": reachable_objects, "segment_size": len(visited)}
        return json.dumps(result, indent=2)
    except Exception as e:
        return json.dumps({"error": str(e), "traceback": traceback.format_exc()})

print(analyze_segment(map_xml_string, input_data['start_x'], input_data['start_y']))
