import json
import xml.etree.ElementTree as ET
import heapq
from collections import deque

def main(map_xml_string, num_turns, badges_obtained_json):
    try:
        root = ET.fromstring(map_xml_string)
        map_id = int(root.get('id'))
        map_width = int(root.get('width'))
        map_height = int(root.get('height'))

        player_pos = None
        tiles = {}
        for row_elem in root.findall('Row'):
            y = int(row_elem.get('id'))
            for tile_elem in row_elem.findall('Tile'):
                x = int(tile_elem.get('id'))
                if tile_elem.find('Player') is not None:
                    player_pos = (x, y)
                tile_type = tile_elem.get('type')
                has_object = tile_elem.get('has-object') == 'true'
                is_warp = tile_elem.get('is-warp') == 'true'
                object_name, object_id = None, None
                for obj_elem in tile_elem.findall('Object'):
                    object_name = obj_elem.get('name') or obj_elem.get('id-name')
                    object_id = obj_elem.get('id')
                    break
                tiles[(x, y)] = {'type': tile_type, 'has_object': has_object, 'is_warp': is_warp, 'object_name': object_name, 'object_id': object_id}

        def a_star_search(start, end_coords, ignore_objects=False):
            open_set = []
            heapq.heappush(open_set, (0, start))
            came_from = {}
            g_score = {tile: float('inf') for tile in tiles}
            g_score[start] = 0
            f_score = {tile: float('inf') for tile in tiles}
            f_score[start] = min(abs(start[0] - end[0]) + abs(start[1] - end[1]) for end in end_coords)

            while open_set:
                _, current = heapq.heappop(open_set)
                if current in end_coords:
                    return True
                for dx, dy in [(0, 1), (0, -1), (1, 0), (-1, 0)]:
                    neighbor = (current[0] + dx, current[1] + dy)
                    if not (1 <= neighbor[0] <= map_width and 1 <= neighbor[1] <= map_height):
                        continue

                    is_neighbor_traversable, _ = is_traversable_check(neighbor[0], neighbor[1], ignore_objects)
                    current_tile_type = tiles.get(current, {}).get('type')
                    neighbor_tile_type = tiles.get(neighbor, {}).get('type')

                    if current_tile_type == 'ledge' and dy == -1: continue
                    if neighbor_tile_type == 'ledge' and dy != 1: continue
                    if not is_neighbor_traversable: continue

                    tentative_g_score = g_score[current] + 1
                    if tentative_g_score < g_score.get(neighbor, float('inf')):
                        came_from[neighbor] = current
                        g_score[neighbor] = tentative_g_score
                        f_score[neighbor] = tentative_g_score + min(abs(neighbor[0] - end[0]) + abs(neighbor[1] - end[1]) for end in end_coords)
                        heapq.heappush(open_set, (f_score[neighbor], neighbor))
            return False

        def is_traversable_check(x, y, ignore_all_objects=False):
            tile_info = tiles.get((x, y))
            if not tile_info: return False, 'Unknown tile'
            tile_type, has_object, object_id = tile_info['type'], tile_info['has_object'], tile_info['object_id']
            traversable_tiles = {'ground', 'grass', 'steps', 'elevated_ground', 'cleared_boulder_barrier', 'ladder_up', 'ladder_down'}

            if tile_type in ('impassable', 'boulder_barrier', 'closed_gate', 'water'): return False, f'{tile_type} at ({x}, {y})'
            if has_object and object_id != '15' and not ignore_all_objects: return False, f'Object at ({x}, {y})'
            if tile_type in traversable_tiles or tile_type == 'ledge': return True, None
            return False, f'Unhandled terrain: {tile_type} at ({x}, {y})'

        navigable_warps = []
        for (x, y), tile_info in tiles.items():
            if tile_info['is_warp']:
                is_directly_traversable, _ = is_traversable_check(x, y, False)
                if is_directly_traversable and a_star_search(player_pos, [(x, y)], False):
                    navigable_warps.append({'x': x, 'y': y})
                else:
                    adjacent_goals = []
                    for dx, dy in [(0, 1), (0, -1), (1, 0), (-1, 0)]:
                        adj_x, adj_y = x + dx, y + dy
                        is_adj_traversable, _ = is_traversable_check(adj_x, adj_y, False)
                        if is_adj_traversable:
                            adjacent_goals.append((adj_x, adj_y))
                    if adjacent_goals and a_star_search(player_pos, adjacent_goals, False):
                        navigable_warps.append({'x': x, 'y': y})

        unseen_tiles = [pos for pos, info in tiles.items() if info['type'] == 'unknown']
        reachable_unseen_tiles_count = 0
        for x, y in unseen_tiles:
            adj_goals = []
            for dx, dy in [(0, 1), (0, -1), (1, 0), (-1, 0)]:
                adj_x, adj_y = x + dx, y + dy
                is_adj_traversable, _ = is_traversable_check(adj_x, adj_y, True)
                if is_adj_traversable:
                    adj_goals.append((adj_x, adj_y))
            if adj_goals and a_star_search(player_pos, adj_goals, True):
                reachable_unseen_tiles_count += 1

        warp_coords = {pos for pos, info in tiles.items() if info['is_warp']}
        visited_warps = set()
        warp_groups = []
        for x, y in warp_coords:
            if (x, y) not in visited_warps:
                current_group = set()
                q = deque([(x, y)])
                visited_warps.add((x, y))
                while q:
                    cx, cy = q.popleft()
                    current_group.add((cx, cy))
                    for dx, dy in [(0, 1), (0, -1), (1, 0), (-1, 0)]:
                        nx, ny = cx + dx, cy + dy
                        if (nx, ny) in warp_coords and (nx, ny) not in visited_warps:
                            visited_warps.add((nx, ny))
                            q.append((nx, ny))
                warp_groups.append(current_group)
        num_warp_exits = len(warp_groups)

        connections_str = root.find('MapConnections').text if root.find('MapConnections') is not None else '[]'
        connections = json.loads(connections_str)
        reachable_connections_count = sum(1 for c in connections if c['reachable'])
        num_exits = num_warp_exits + reachable_connections_count
        is_in_dead_end_area = num_exits <= 1 and reachable_unseen_tiles_count == 0

        result = {
            "current_map_id": map_id,
            "current_position": {"x": player_pos[0], "y": player_pos[1]},
            "num_turns": int(num_turns),
            "badges_obtained": json.loads(badges_obtained_json),
            "is_in_dead_end_area": is_in_dead_end_area,
            "navigable_warps": navigable_warps,
            "reachable_unseen_tiles_count": reachable_unseen_tiles_count
        }
    except Exception as e:
        result = {"error": str(e), "traceback": repr(e)}
    print(json.dumps(result))

main(map_xml_string, input_data['num_turns'], input_data['badges_obtained_json'])
