import xml.etree.ElementTree as ET
import heapq

def parse_map_xml(xml_string):
    root = ET.fromstring(xml_string)
    width = int(root.get('width'))
    height = int(root.get('height'))
    grid = [['' for _ in range(width)] for _ in range(height)]
    objects = {}

    for row_elem in root.findall('Row'):
        y = int(row_elem.get('id')) - 1
        for tile_elem in row_elem.findall('Tile'):
            x = int(tile_elem.get('id')) - 1
            grid[y][x] = tile_elem.get('type')
            if tile_elem.find('Object') is not None and tile_elem.find('Object').get('id-name') != 'Pikachu':
                objects[(x + 1, y + 1)] = True
    return grid, objects, width, height

def is_valid(x, y, width, height):
    return 1 <= x <= width and 1 <= y <= height

def heuristic(a, b):
    return abs(a[0] - b[0]) + abs(a[1] - b[1])

def is_traversable(x, y, grid, objects, movement_type):
    if (x, y) in objects:
        return False
    tile_type = grid[y-1][x-1]

    if tile_type in ['impassable', 'boulder_barrier', 'closed_gate', 'unknown']:
        return False

    if movement_type == 'walking':
        return tile_type in ['ground', 'grass', 'steps', 'elevated_ground', 'cleared_boulder_barrier', 'open_gate', 'gate_offscreen', 'boulder_switch', 'spinner_stop'] or tile_type.startswith('spinner')
    elif movement_type == 'surfing':
        return tile_type == 'water'
    return False

def get_neighbors(current_pos, grid, objects, width, height, movement_type):
    x, y = current_pos
    neighbors = []
    for dx, dy, move in [(0, -1, 'Up'), (0, 1, 'Down'), (-1, 0, 'Left'), (1, 0, 'Right')]:
        nx, ny = x + dx, y + dy
        if is_valid(nx, ny, width, height):
            if grid[ny-1][nx-1] == 'ledge':
                if dy == 1:
                    if is_valid(nx, ny + 1, width, height) and is_traversable(nx, ny + 1, grid, objects, movement_type):
                        neighbors.append(((nx, ny + 1), move, 1))
                continue

            if is_traversable(nx, ny, grid, objects, movement_type):
                neighbors.append(((nx, ny), move, 1))
    return neighbors

def a_star_search(start, target, grid, objects, width, height, movement_type):
    target_is_impassable = not is_traversable(target[0], target[1], grid, objects, movement_type)

    open_set = [(0, start)]
    came_from = {}
    g_score = {start: 0}
    f_score = {start: heuristic(start, target)}

    while open_set:
        _, current = heapq.heappop(open_set)

        if not target_is_impassable and current == target:
            return reconstruct_path(came_from, current)

        if target_is_impassable and heuristic(current, target) == 1:
             return reconstruct_path(came_from, current)

        for neighbor_pos, move, cost in get_neighbors(current, grid, objects, width, height, movement_type):
            tentative_g_score = g_score[current] + cost
            if tentative_g_score < g_score.get(neighbor_pos, float('inf')):
                came_from[neighbor_pos] = (current, move)
                g_score[neighbor_pos] = tentative_g_score
                f_score[neighbor_pos] = tentative_g_score + heuristic(neighbor_pos, target)
                heapq.heappush(open_set, (f_score[neighbor_pos], neighbor_pos))

    return None

def reconstruct_path(came_from, current):
    path = []
    while current in came_from:
        current, move = came_from[current]
        path.append(move)
    return path[::-1]

