import json
import xml.etree.ElementTree as ET
from collections import deque

# Extract arguments from input_data
start_x = int(input_data['start_x'])
start_y = int(input_data['start_y'])
target_x = int(input_data['target_x'])
target_y = int(input_data['target_y'])
mode = input_data.get('mode', 'walking')

def bfs_path(start_x, start_y, target_x, target_y, mode, map_xml_str):
    try:
        root = ET.fromstring(map_xml_str)
    except Exception as e:
        return []

    grid = {}
    for row in root.findall('Row'):
        r_id = int(row.attrib['id'])
        for tile in row.findall('Tile'):
            c_id = int(tile.attrib['id'])
            t_type = tile.attrib.get('type', 'FLOOR')

            # Check for blocking objects
            has_blocking_object = False
            for child in tile:
                if child.tag == 'Object':
                    has_blocking_object = True
                    break

            grid[(c_id, r_id)] = {
                'type': t_type,
                'blocked': has_blocking_object
            }

    queue = deque([(start_x, start_y, [])])
    visited = set([(start_x, start_y)])

    # max iterations to prevent timeout
    iterations = 0
    max_iterations = 5000 

    while queue:
        cx, cy, path = queue.popleft()
        iterations += 1
        if iterations > max_iterations:
            break

        if cx == target_x and cy == target_y:
            return path

        # Directions: Up, Down, Left, Right
        moves = [
            (0, -1, 'Up'),
            (0, 1, 'Down'),
            (-1, 0, 'Left'),
            (1, 0, 'Right')
        ]

        for dx, dy, btn in moves:
            nx, ny = cx + dx, cy + dy

            if (nx, ny) in visited:
                continue

            if (nx, ny) not in grid:
                continue

            cell = grid[(nx, ny)]

            # Check Object Collision
            # Allow moving onto target even if it has an object (interaction)
            if cell['blocked'] and (nx != target_x or ny != target_y):
                continue

            ctype = cell['type']

            # Standard Walls
            if ctype in ['WALL', 'VOID', 'TREE', 'ROCK', 'BOULDER', 'PC', 'SIGN', 'COUNTER', 'WINDOW']:
                continue

            # Ledges (One Way Down)
            if ctype == 'LEDGE_HOP_DOWN':
                if dy != 1: # Must move Down
                    continue

            # Floor Up Wall (One Way Down entry)
            if ctype == 'FLOOR_UP_WALL':
                if dy == 1: # Cannot enter from North (moving Down)
                    continue

            # Water Logic
            is_water = ctype in ['WATER', 'BUOY']
            if mode == 'walking':
                if is_water:
                    continue
            elif mode == 'surfing':
                # Simplified surfing logic: blocked by land unless it's the target
                if not is_water and (nx != target_x or ny != target_y):
                     continue

            visited.add((nx, ny))
            queue.append((nx, ny, path + [btn]))

    return []

print(json.dumps(bfs_path(start_x, start_y, target_x, target_y, mode, map_xml_string)))
